pragma solidity 0.7.6;
pragma abicoder v2;

import "@uniswap/v3-periphery/contracts/interfaces/INonfungiblePositionManager.sol";
import "@uniswap/v3-core/contracts/interfaces/IUniswapV3Pool.sol";
import "@openzeppelin/contracts/math/SafeMath.sol";
import "./BaseModule.sol";
import "../libraries/SafeInt24Math.sol";
import "../libraries/UniswapHelper.sol";
import "../libraries/MathHelper.sol";
import "../interfaces/modules/IIdleLiquidityModule.sol";
import "../interfaces/IPositionManagerFactory.sol";
import "../interfaces/IPositionManager.sol";
import "../interfaces/IUniswapAddressHolder.sol";
import "../interfaces/actions/IClosePosition.sol";
import "../interfaces/actions/IRepayRebalanceFee.sol";
import "../interfaces/actions/ISwapToPositionRatio.sol";
import "../interfaces/actions/IMint.sol";

///@title Idle Liquidity Module to manage liquidity for a user position
contract IdleLiquidityModule is BaseModule, IIdleLiquidityModule {
    ///@notice uniswap address holder
    IUniswapAddressHolder public immutable uniswapAddressHolder;
    using SafeMath for uint256;
    using SafeInt24Math for int24;

    ///@notice emitted when a position is rebalanced
    ///@param positionManager address of the called position manager
    ///@param closedTokenId closed tokenId
    ///@param mintedTokenId minted tokenId
    ///@param collectedFee0 amount of token0 collected
    ///@param collectedFee1 amount of token1 collected
    event positionRebalanced(
        address indexed positionManager,
        uint256 closedTokenId,
        uint256 mintedTokenId,
        uint256 collectedFee0,
        uint256 collectedFee1
    );

    ///@notice assing the uniswap address holder to the contract
    ///@param _registry address of the registry
    ///@param _uniswapAddressHolder address of the uniswap address holder
    constructor(address _registry, address _uniswapAddressHolder) BaseModule(_registry) {
        require(_uniswapAddressHolder != address(0), "ILCA0");
        require(_registry != address(0), "ILCA0");

        uniswapAddressHolder = IUniswapAddressHolder(_uniswapAddressHolder);
    }

    ///@notice check if the position is out of range and rebalance it by swapping the tokens as necessary
    ///@param input RebalanceInput struct
    function rebalance(RebalanceInput calldata input) external onlyWhitelistedKeeper {
        address positionManager = IPositionManagerFactory(registry.positionManagerFactoryAddress())
            .userToPositionManager(input.userAddress);
        require(positionManager != address(0), "ILPM0");

        IPositionManager.PositionInfo memory pInfo = IPositionManager(positionManager).getPositionInfo(
            input.positionId
        );

        _checkCurrentTickOutOfRange(pInfo.tokenId);

        uint256 amount0CollectedFee;
        uint256 amount1CollectedFee;
        uint256 newTokenId;

        {
            uint256 amount0Removed;
            uint256 amount1Removed;

            (amount0CollectedFee, amount1CollectedFee, amount0Removed, amount1Removed) = _closedAndRepayRebalance(
                _CloseAndRepayRebalanceParams(positionManager, pInfo.tokenId, input.estimatedGasFee, input.isForced)
            );

            require(amount0Removed > 0 || amount1Removed > 0, "ILAR");

            uint256 amount0Leftover;
            uint256 amount1Leftover;
            (newTokenId, amount0Leftover, amount1Leftover) = _swapAndMint(
                _SwapAndMintParams(
                    positionManager,
                    pInfo.tokenId,
                    amount0Removed.add(amount0CollectedFee).add(pInfo.amount0Leftover),
                    amount1Removed.add(amount1CollectedFee).add(pInfo.amount1Leftover),
                    pInfo.tickLowerDiff,
                    pInfo.tickLowerDiff
                )
            );

            pInfo.amount0CollectedFee = pInfo.amount0CollectedFee.add(amount0CollectedFee);
            pInfo.amount1CollectedFee = pInfo.amount1CollectedFee.add(amount1CollectedFee);

            IPositionManager(positionManager).middlewareRebalance(
                input.positionId,
                newTokenId,
                pInfo.tickLowerDiff,
                pInfo.tickUpperDiff,
                pInfo.amount0CollectedFee,
                pInfo.amount1CollectedFee,
                amount0Leftover,
                amount1Leftover
            );
        }

        emit positionRebalanced(positionManager, pInfo.tokenId, newTokenId, amount0CollectedFee, amount1CollectedFee);
    }

    ///@notice check if the position is out of range and rebalance it by swapping the tokens as necessary and customize lower & upper tick diff
    ///@param input RebalanceWithTickDiffsInput struct
    function rebalanceWithTickDiffs(RebalanceWithTickDiffsInput calldata input) external onlyWhitelistedKeeper {
        require(input.tickLowerDiff <= 0 && input.tickUpperDiff >= 0, "ILTD");

        address positionManager = IPositionManagerFactory(registry.positionManagerFactoryAddress())
            .userToPositionManager(input.userAddress);
        require(positionManager != address(0), "ILPM0");

        IPositionManager.PositionInfo memory pInfo = IPositionManager(positionManager).getPositionInfo(
            input.positionId
        );

        _checkCurrentTickOutOfRange(pInfo.tokenId);

        uint256 amount0CollectedFee;
        uint256 amount1CollectedFee;
        uint256 newTokenId;

        {
            uint256 amount0Removed;
            uint256 amount1Removed;
            (amount0CollectedFee, amount1CollectedFee, amount0Removed, amount1Removed) = _closedAndRepayRebalance(
                _CloseAndRepayRebalanceParams(positionManager, pInfo.tokenId, input.estimatedGasFee, input.isForced)
            );

            require(amount0Removed > 0 || amount1Removed > 0, "ILAR");

            uint256 amount0Leftover;
            uint256 amount1Leftover;
            (newTokenId, amount0Leftover, amount1Leftover) = _swapAndMint(
                _SwapAndMintParams(
                    positionManager,
                    pInfo.tokenId,
                    amount0Removed.add(amount0CollectedFee).add(pInfo.amount0Leftover),
                    amount1Removed.add(amount1CollectedFee).add(pInfo.amount1Leftover),
                    input.tickLowerDiff,
                    input.tickUpperDiff
                )
            );

            pInfo.amount0CollectedFee = pInfo.amount0CollectedFee.add(amount0CollectedFee);
            pInfo.amount1CollectedFee = pInfo.amount1CollectedFee.add(amount1CollectedFee);

            IPositionManager(positionManager).middlewareRebalance(
                input.positionId,
                newTokenId,
                input.tickLowerDiff,
                input.tickUpperDiff,
                pInfo.amount0CollectedFee,
                pInfo.amount1CollectedFee,
                amount0Leftover,
                amount1Leftover
            );
        }

        emit positionRebalanced(positionManager, pInfo.tokenId, newTokenId, amount0CollectedFee, amount1CollectedFee);
    }

    ///@notice rebalances the position by swapping the tokens
    ///@param positionManager address of the position manager
    ///@param tokenId tokenId of the position
    ///@param tickLower new tickLower of the position
    ///@param tickUpper new tickUpper of the position
    struct _CloseAndRepayRebalanceParams {
        address positionManager;
        uint256 tokenId;
        uint256 rebalanceFee;
        bool isForced;
    }

    function _closedAndRepayRebalance(
        _CloseAndRepayRebalanceParams memory params
    )
        internal
        returns (
            uint256 amount0CollectedFee,
            uint256 amount1CollectedFee,
            uint256 amount0Removed,
            uint256 amount1Removed
        )
    {
        (address token0, address token1, , , ) = UniswapHelper._getTokens(
            params.tokenId,
            INonfungiblePositionManager(uniswapAddressHolder.nonfungiblePositionManagerAddress())
        );

        ///@dev call closePositionAction
        (amount0CollectedFee, amount1CollectedFee, amount0Removed, amount1Removed) = IClosePosition(
            params.positionManager
        ).closePosition(params.tokenId, false);

        ///@dev call repayRebalanceFeeAction
        (uint256 amount0Repay, uint256 amount1Repay, ) = IRepayRebalanceFee(params.positionManager).repayRebalanceFee(
            IRepayRebalanceFee.RepayRebalanceFeeInput({
                token0: token0,
                token1: token1,
                amount0UsedToRepay: params.isForced ? amount0CollectedFee.add(amount0Removed) : amount0CollectedFee,
                amount1UsedToRepay: params.isForced ? amount1CollectedFee.add(amount1Removed) : amount1CollectedFee,
                rebalanceFee: params.rebalanceFee,
                receiver: msg.sender
            })
        );

        if (amount0Repay > amount0CollectedFee) {
            amount0CollectedFee = 0;
            amount0Removed = amount0Removed.sub(amount0Repay.sub(amount0CollectedFee));
        } else {
            amount0CollectedFee = amount0CollectedFee.sub(amount0Repay);
        }

        if (amount1Repay > amount1CollectedFee) {
            amount1CollectedFee = 0;
            amount1Removed = amount1Removed.sub(amount1Repay.sub(amount1CollectedFee));
        } else {
            amount1CollectedFee = amount1CollectedFee.sub(amount1Repay);
        }
    }

    ///@notice rebalances the position by swapping the tokens
    ///@param positionManager address of the position manager
    ///@param tokenId tokenId of the position
    ///@param amount0 amount0 to be swapped and mint
    ///@param amount1 amount1 to be swapped and mint
    ///@param tickLowerDiff diff of tickLower of the position
    ///@param tickUpperDiff diff of tickUpper of the position
    struct _SwapAndMintParams {
        address positionManager;
        uint256 tokenId;
        uint256 amount0;
        uint256 amount1;
        int24 tickLowerDiff;
        int24 tickUpperDiff;
    }

    function _swapAndMint(
        _SwapAndMintParams memory params
    ) internal returns (uint256 newTokenId, uint256 amount0LeftOver, uint256 amount1LeftOver) {
        (address token0, address token1, uint24 fee, , ) = UniswapHelper._getTokens(
            params.tokenId,
            INonfungiblePositionManager(uniswapAddressHolder.nonfungiblePositionManagerAddress())
        );

        uint256 amount0Deposit;
        uint256 amount1Deposit;
        ///@dev call swapToPositionAction to perform the swap
        (uint256 amount0AfterSwapped, uint256 amount1AfterSwapped) = ISwapToPositionRatio(params.positionManager)
            .swapToPositionRatio(
                ISwapToPositionRatio.SwapToPositionInput({
                    token0Address: token0,
                    token1Address: token1,
                    fee: fee,
                    amount0In: params.amount0,
                    amount1In: params.amount1,
                    tickLower: _getCurrentTick(token0, token1, fee).add(params.tickLowerDiff),
                    tickUpper: _getCurrentTick(token0, token1, fee).add(params.tickUpperDiff)
                })
            );

        ///@dev call mintAction
        (newTokenId, amount0Deposit, amount1Deposit) = IMint(params.positionManager).mint(
            IMint.MintInput({
                token0Address: token0,
                token1Address: token1,
                fee: fee,
                tickLower: _getCurrentTick(token0, token1, fee).add(params.tickLowerDiff),
                tickUpper: _getCurrentTick(token0, token1, fee).add(params.tickUpperDiff),
                amount0Desired: amount0AfterSwapped,
                amount1Desired: amount1AfterSwapped,
                isReturnLeftOver: false
            })
        );

        ///@dev cal leftOver
        amount0LeftOver = amount0AfterSwapped.sub(amount0Deposit);
        amount1LeftOver = amount1AfterSwapped.sub(amount1Deposit);
    }

    function _checkCurrentTickOutOfRange(uint256 tokenId) internal view {
        (address token0, address token1, uint24 fee, int24 tickLower, int24 tickUpper) = UniswapHelper._getTokens(
            tokenId,
            INonfungiblePositionManager(uniswapAddressHolder.nonfungiblePositionManagerAddress())
        );

        int24 currentTick = _getCurrentTick(token0, token1, fee);

        require(currentTick < tickLower || currentTick > tickUpper, "ILOOR");
    }

    function _getCurrentTick(address token0, address token1, uint24 fee) internal view returns (int24 currentTick) {
        IUniswapV3Pool pool = IUniswapV3Pool(
            UniswapHelper._getPool(uniswapAddressHolder.uniswapV3FactoryAddress(), token0, token1, fee)
        );
        (, currentTick, , , , , ) = pool.slot0();
    }
}
