pragma solidity 0.7.6;

library MathHelper {
    ///@dev cast uint24 to int24
    function fromUint24ToInt24(uint24 value) internal pure returns (int24) {
        require(uint256(value) <= uint256(type(int24).max), 'MH1');
        return int24(value);
    }

    ///@dev cast int24 to uint24
    function fromInt24ToUint24(int24 value) internal pure returns (uint24) {
        require(int256(value) <= int256(type(uint24).max), 'MH2');
        return value >= 0 ? uint24(value) : uint24(-value);
    }

    ///@dev cast uint256 to uint24
    function fromUint256ToUint24(uint256 value) internal pure returns (uint24 out) {
        require((out = uint24(value)) == value, 'MH3');
    }

    ///@dev cast uint256 to int24
    function fromUint256ToInt24(uint256 value) internal pure returns (int24) {
        require(value <= uint256(type(int24).max), 'MH4');
        return int24(value);
    }

    ///@dev cast uint56 to int24
    function fromInt56ToInt24(int56 value) internal pure returns (int24 out) {
        require((out = int24(value)) == value, 'MH5');
    }
}
