pragma solidity 0.7.6;
pragma abicoder v2;

import "../Storage.sol";
import "../interfaces/actions/IRepayRebalanceFeeFromManager.sol";

///@notice action to repay rebalance fee
contract RepayRebalanceFeeFromManager is IRepayRebalanceFeeFromManager {
    ///@notice repay rebalance fee to receiver
    ///@param inputs struct of repayRebalanceFeeFromManager parameters
    function repayRebalanceFeeFromManager(
        IRepayRebalanceFeeFromManager.RepayRebalanceFeeFromManagerInput calldata inputs
    ) external payable override {
        require(address(this).balance >= inputs.rebalanceFee, "RRFFMIB");

        ///@dev repay rebalance fee
        if (inputs.rebalanceFee > 0) {
            (bool sent, ) = inputs.receiver.call{ value: inputs.rebalanceFee }("");

            require(sent, "RRFFMSE");
        }
        emit RebalanceFeeRepaid(address(this), inputs.rebalanceFee, inputs.receiver);
    }
}
