// SPDX-License-Identifier: GPL-2.0

pragma solidity 0.7.6;
pragma abicoder v2;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/SafeERC20.sol";
import "@openzeppelin/contracts/math/SafeMath.sol";
import "@uniswap/v3-periphery/contracts/interfaces/INonfungiblePositionManager.sol";
import "../libraries/ERC20Helper.sol";
import "../libraries/UniswapHelper.sol";
import "../Storage.sol";
import "../interfaces/IPositionManager.sol";
import "../interfaces/actions/IMint.sol";

///@notice action to mint a UniswapV3 position NFT
contract Mint is IMint {
    using SafeERC20 for IERC20;
    using SafeMath for uint256;

    ///@notice emitted when a UniswapNFT is deposited in PositionManager
    ///@param positionManager address of PositionManager
    ///@param tokenId Id of deposited token
    ///@param amount0Deposited token0 amount deposited
    ///@param amount1Deposited token1 amount deposited
    event PositionMinted(
        address indexed positionManager,
        uint256 tokenId,
        uint256 amount0Deposited,
        uint256 amount1Deposited
    );

    ///@notice mints a UniswapV3 position NFT
    ///@param inputs struct of MintInput parameters
    ///@return tokenId ID of the minted NFT
    ///@return amount0Deposited token0 amount deposited
    ///@return amount1Deposited token1 amount deposited
    function mint(
        MintInput calldata inputs
    ) external override returns (uint256 tokenId, uint256 amount0Deposited, uint256 amount1Deposited) {
        StorageStruct storage Storage = PositionManagerStorage.getStorage();

        address nonfungiblePositionManagerAddress = Storage.uniswapAddressHolder.nonfungiblePositionManagerAddress();

        ERC20Helper._approveToken(inputs.token0Address, nonfungiblePositionManagerAddress, inputs.amount0Desired);
        ERC20Helper._approveToken(inputs.token1Address, nonfungiblePositionManagerAddress, inputs.amount1Desired);

        (tokenId, , amount0Deposited, amount1Deposited) = INonfungiblePositionManager(nonfungiblePositionManagerAddress)
            .mint(
                INonfungiblePositionManager.MintParams({
                    token0: inputs.token0Address,
                    token1: inputs.token1Address,
                    fee: inputs.fee,
                    tickLower: inputs.tickLower,
                    tickUpper: inputs.tickUpper,
                    amount0Desired: inputs.amount0Desired,
                    amount1Desired: inputs.amount1Desired,
                    amount0Min: 0,
                    amount1Min: 0,
                    recipient: address(this),
                    deadline: block.timestamp
                })
            );

        ///@dev send leftover tokens back to the user if necessary
        if (inputs.isReturnLeftOver) {
            uint256 amount0Leftover = inputs.amount0Desired.sub(amount0Deposited);
            uint256 amount1Leftover = inputs.amount1Desired.sub(amount1Deposited);
            if (amount0Leftover != 0) IERC20(inputs.token0Address).safeTransfer(Storage.owner, amount0Leftover);
            if (amount1Leftover != 0) IERC20(inputs.token1Address).safeTransfer(Storage.owner, amount1Leftover);
        }

        emit PositionMinted(address(this), tokenId, amount0Deposited, amount1Deposited);
    }
}

contracts/actions/RepayRebalanceFee.sol.content:
// SPDX-License-Identifier: GPL-2.0

pragma solidity 0.7.6;
pragma abicoder v2;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/SafeERC20.sol";
import "@openzeppelin/contracts/math/SafeMath.sol";
import "@uniswap/v3-periphery/contracts/interfaces/ISwapRouter.sol";
import "@uniswap/v3-core/contracts/interfaces/IUniswapV3Pool.sol";
import "../libraries/ERC20Helper.sol";
import "../libraries/UniswapHelper.sol";
import "../libraries/UniswapQuoter.sol";
import "../libraries/SwapHelper.sol";
import "../Storage.sol";
import "../interfaces/actions/IRepayRebalanceFee.sol";
import "../interfaces/IRegistry.sol";
import "../interfaces/IERC20Extended.sol";
import "../interfaces/IWETH9.sol";

///@notice action to repay rebalance fee
contract RepayRebalanceFee is IRepayRebalanceFee {
    using SafeERC20 for IERC20;
    using SafeMath for uint256;

    ///@notice emitted when repaying rebalance fee to keeper
    event RepayRebalanceFeeToKeeper(
        address indexed positionManager,
        uint256 token0Repay,
        uint256 token1Repay,
        uint256 totalWETH9Repay
    );

    ///@notice repay rebalance fee to keeper
    ///@param inputs struct of repayRebalanceFee parameters
    function repayRebalanceFee(
        IRepayRebalanceFee.RepayRebalanceFeeInput calldata inputs
    ) external payable override returns (uint256 token0Repay, uint256 token1Repay, uint256 totalWETH9Repay) {
        StorageStruct storage Storage = PositionManagerStorage.getStorage();
        uint24[] memory allowableFeeTiers = Storage.registry.getAllowableFeeTiers();

        address weth9MiddleTokenDeepestPool = UniswapHelper._findV3DeepestPool(
            Storage.uniswapAddressHolder.uniswapV3FactoryAddress(),
            Storage.registry.weth9(),
            Storage.registry.usdValueTokenAddress(),
            allowableFeeTiers
        );

        uint256 weth9MiddleTokenValue;
        {
            // scope to avoid stack too deep errors
            (uint160 weth9MiddleTokenSqrtPriceX96, , , , , , ) = IUniswapV3Pool(weth9MiddleTokenDeepestPool).slot0();
            weth9MiddleTokenValue = UniswapQuoter.quoteWithSqrtPrice(
                Storage.registry.weth9(),
                Storage.registry.usdValueTokenAddress(),
                inputs.rebalanceFee,
                weth9MiddleTokenSqrtPriceX96
            );
        }

        address token0MiddleTokenDeepestPool;
        address token1MiddleTokenDeepestPool;
        {
            // scope to avoid stack too deep errors
            uint256 token0MiddleTokenPrice;
            if (inputs.token0 != Storage.registry.usdValueTokenAddress()) {
                token0MiddleTokenDeepestPool = UniswapHelper._findV3DeepestPool(
                    Storage.uniswapAddressHolder.uniswapV3FactoryAddress(),
                    inputs.token0,
                    Storage.registry.usdValueTokenAddress(),
                    allowableFeeTiers
                );
                (uint160 sqrtPriceX96, , , , , , ) = IUniswapV3Pool(token0MiddleTokenDeepestPool).slot0();

                token0MiddleTokenPrice = SwapHelper.getPrice(
                    sqrtPriceX96,
                    inputs.token0,
                    Storage.registry.usdValueTokenAddress()
                );
            } else {
                token0MiddleTokenPrice = SwapHelper.getPriceWithSameToken(inputs.token0);
            }

            uint256 token1MiddleTokenPrice;
            if (inputs.token1 != Storage.registry.usdValueTokenAddress()) {
                token1MiddleTokenDeepestPool = UniswapHelper._findV3DeepestPool(
                    Storage.uniswapAddressHolder.uniswapV3FactoryAddress(),
                    inputs.token1,
                    Storage.registry.usdValueTokenAddress(),
                    allowableFeeTiers
                );
                uint160 sqrtPriceX96;

                (sqrtPriceX96, , , , , , ) = IUniswapV3Pool(token1MiddleTokenDeepestPool).slot0();

                token1MiddleTokenPrice = SwapHelper.getPrice(
                    sqrtPriceX96,
                    inputs.token1,
                    Storage.registry.usdValueTokenAddress()
                );
            } else {
                token1MiddleTokenPrice = SwapHelper.getPriceWithSameToken(inputs.token1);
            }

            (token0Repay, token1Repay) = SwapHelper.distributeTargetAmount(
                inputs.token0,
                inputs.token1,
                inputs.amount0UsedToRepay,
                inputs.amount1UsedToRepay,
                token0MiddleTokenPrice,
                token1MiddleTokenPrice,
                weth9MiddleTokenValue
            );

            require(token0Repay <= inputs.amount0UsedToRepay, "RRFR");
            require(token1Repay <= inputs.amount1UsedToRepay, "RRFR");
        }

        {
            // scope to avoid stack too deep errors
            uint256 totalDeductMiddleAmount;
            if (token0Repay > 0) {
                if (inputs.token0 == Storage.registry.weth9()) {
                    totalWETH9Repay = totalWETH9Repay.add(token0Repay);
                } else if (inputs.token0 == Storage.registry.usdValueTokenAddress()) {
                    totalDeductMiddleAmount = totalDeductMiddleAmount.add(token0Repay);
                } else {
                    uint256 deductMiddleAmount0Out = _swap(
                        Storage.uniswapAddressHolder.swapRouterAddress(),
                        token0MiddleTokenDeepestPool,
                        inputs.token0,
                        Storage.registry.usdValueTokenAddress(),
                        token0Repay,
                        Storage.registry.maxTwapDeviation(),
                        Storage.registry.twapDuration()
                    );

                    totalDeductMiddleAmount = totalDeductMiddleAmount.add(deductMiddleAmount0Out);
                }
            }

            if (token1Repay > 0) {
                if (inputs.token0 == Storage.registry.weth9()) {
                    totalWETH9Repay = totalWETH9Repay.add(token1Repay);
                } else if (inputs.token0 == Storage.registry.usdValueTokenAddress()) {
                    totalDeductMiddleAmount = totalDeductMiddleAmount.add(token1Repay);
                } else {
                    uint256 deductMiddleAmount1Out = _swap(
                        Storage.uniswapAddressHolder.swapRouterAddress(),
                        token1MiddleTokenDeepestPool,
                        inputs.token1,
                        Storage.registry.usdValueTokenAddress(),
                        token1Repay,
                        Storage.registry.maxTwapDeviation(),
                        Storage.registry.twapDuration()
                    );

                    totalDeductMiddleAmount = totalDeductMiddleAmount.add(deductMiddleAmount1Out);
                }
            }

            if (totalDeductMiddleAmount > 0) {
                uint256 deductWETH9AmountOut = _swap(
                    Storage.uniswapAddressHolder.swapRouterAddress(),
                    weth9MiddleTokenDeepestPool,
                    Storage.registry.usdValueTokenAddress(),
                    Storage.registry.weth9(),
                    totalDeductMiddleAmount,
                    Storage.registry.maxTwapDeviation(),
                    Storage.registry.twapDuration()
                );

                totalWETH9Repay = totalWETH9Repay.add(deductWETH9AmountOut);
            }
        }

        ///@dev repay rebalance fee
        if (totalWETH9Repay > 0) {
            ///@dev withdraw weth9
            IWETH9(Storage.registry.weth9()).withdraw(totalWETH9Repay);

            (bool sent, ) = inputs.receiver.call{ value: totalWETH9Repay }("");
            require(sent, "RBFSE");

            IERC20(Storage.registry.weth9()).safeTransfer(inputs.receiver, totalWETH9Repay);
        }

        emit RepayRebalanceFeeToKeeper(address(this), token0Repay, token1Repay, totalWETH9Repay);
    }

    function _swap(
        address swapRouterAddress,
        address deepestPool,
        address tokenIn,
        address tokenOut,
        uint256 amountIn,
        int24 maxTwapDeviation,
        uint32 twapDuration
    ) internal returns (uint256 amountOut) {
        SwapHelper.checkDeviation(IUniswapV3Pool(deepestPool), maxTwapDeviation, twapDuration);

        ERC20Helper._approveToken(tokenIn, swapRouterAddress, amountIn);

        amountOut = ISwapRouter(swapRouterAddress).exactInputSingle(
            ISwapRouter.ExactInputSingleParams({
                tokenIn: tokenIn,
                tokenOut: tokenOut,
                fee: IUniswapV3Pool(deepestPool).fee(),
                recipient: address(this),
                deadline: block.timestamp,
                amountIn: amountIn,
                // TODO: slippage protection by using amountOutMinimum, sqrtPriceLimitX96
                amountOutMinimum: 0,
                sqrtPriceLimitX96: 0
            })
        );
    }
}

contracts/actions/ReturnProfit.sol.content:
// SPDX-License-Identifier: GPL-2.0

pragma solidity 0.7.6;
pragma abicoder v2;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/SafeERC20.sol";
import "@openzeppelin/contracts/math/SafeMath.sol";
import "@uniswap/v3-periphery/contracts/interfaces/ISwapRouter.sol";
import "@uniswap/v3-core/contracts/interfaces/IUniswapV3Pool.sol";
import "../libraries/ERC20Helper.sol";
import "../libraries/UniswapHelper.sol";
import "../libraries/SwapHelper.sol";
import "../Storage.sol";
import "../interfaces/actions/IReturnProfit.sol";
import "../interfaces/IRegistry.sol";

///@notice action to return profit
contract ReturnProfit is IReturnProfit {
    using SafeERC20 for IERC20;
    using SafeMath for uint256;

    ///@notice emitted when profit is returned
    event ProfitReturned(address indexed positionManager, uint256 returnedAmount0, uint256 returnedAmount1);

    ///@notice return profit
    ///@param inputs struct of returnProfit parameters
    function returnProfit(
        IReturnProfit.ReturnProfitInput calldata inputs
    ) external override returns (IReturnProfit.ReturnProfitOutput memory outputs) {
        StorageStruct storage Storage = PositionManagerStorage.getStorage();
        uint24[] memory allowableFeeTiers = Storage.registry.getAllowableFeeTiers();

        address token0token1DeepestPool = UniswapHelper._findV3DeepestPool(
            Storage.uniswapAddressHolder.uniswapV3FactoryAddress(),
            inputs.token0,
            inputs.token1,
            allowableFeeTiers
        );

        (outputs.amount0Returned, outputs.amount1Returned) = _determineTokensReceived(
            token0token1DeepestPool,
            inputs.token0,
            inputs.token1,
            inputs.returnedToken,
            inputs.amount0,
            inputs.amount1
        );

        if (outputs.amount0Returned > 0) {
            IERC20(inputs.token0).safeTransfer(Storage.owner, outputs.amount0Returned);
        }

        if (outputs.amount1Returned > 0) {
            IERC20(inputs.token1).safeTransfer(Storage.owner, outputs.amount1Returned);
        }

        emit ProfitReturned(address(this), outputs.amount0Returned, outputs.amount1Returned);
    }

    function _determineTokensReceived(
        address deepestPool,
        address token0,
        address token1,
        address receivedToken,
        uint256 amount0,
        uint256 amount1
    ) internal returns (uint256 amount0Received, uint256 amount1Received) {
        if (receivedToken == token0) {
            if (amount1 == 0) {
                amount0Received = amount0;
            } else {
                amount0Received = amount0.add(_swap(deepestPool, token1, receivedToken, amount1));
            }
        } else if (receivedToken == token1) {
            if (amount0 == 0) {
                amount1Received = amount1;
            } else {
                amount1Received = amount1.add(_swap(deepestPool, token0, receivedToken, amount0));
            }
        } else {
            amount0Received = amount0;
            amount1Received = amount1;
        }
    }

    function _swap(
        address deepestPool,
        address tokenIn,
        address tokenOut,
        uint256 amountIn
    ) internal returns (uint256 amountOut) {
        StorageStruct storage Storage = PositionManagerStorage.getStorage();

        SwapHelper.checkDeviation(
            IUniswapV3Pool(deepestPool),
            Storage.registry.maxTwapDeviation(),
            Storage.registry.twapDuration()
        );

        ERC20Helper._approveToken(tokenIn, Storage.uniswapAddressHolder.swapRouterAddress(), amountIn);

        amountOut = ISwapRouter(Storage.uniswapAddressHolder.swapRouterAddress()).exactInputSingle(
            ISwapRouter.ExactInputSingleParams({
                tokenIn: tokenIn,
                tokenOut: tokenOut,
                fee: IUniswapV3Pool(deepestPool).fee(),
                recipient: address(this),
                deadline: block.timestamp,
                amountIn: amountIn,
                // TODO: slippage protection by using amountOutMinimum, sqrtPriceLimitX96
                amountOutMinimum: 0,
                sqrtPriceLimitX96: 0
            })
        );
    }
}

contracts/actions/ShareProfit.sol.content:
// SPDX-License-Identifier: GPL-2.0

pragma solidity 0.7.6;
pragma abicoder v2;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/SafeERC20.sol";
import "@openzeppelin/contracts/math/SafeMath.sol";
import "@uniswap/v3-periphery/contracts/interfaces/ISwapRouter.sol";
import "@uniswap/v3-core/contracts/interfaces/IUniswapV3Pool.sol";
import "../libraries/ERC20Helper.sol";
import "../libraries/UniswapHelper.sol";
import "../libraries/UniswapQuoter.sol";
import "../libraries/SwapHelper.sol";
import "../Storage.sol";
import "../interfaces/actions/IShareProfit.sol";
import "../interfaces/IRegistry.sol";
import "../interfaces/IERC20Extended.sol";

///@notice action to share profit
contract ShareProfit is IShareProfit {
    using SafeERC20 for IERC20;
    using SafeMath for uint256;

    ///@notice emitted when profit is shared
    event ProfitShared(
        address indexed positionManager,
        address performanceFeeRecipient,
        uint256 performanceFeeAmount0,
        uint256 performanceFeeAmount1,
        uint256 serviceFeeAmount0,
        uint256 serviceFeeAmount1,
        uint256 returnedAmount0,
        uint256 returnedAmount1
    );

    ///@notice share profit
    ///@param inputs struct of shareProfit parameters
    function shareProfit(
        ShareProfitInput calldata inputs
    ) external override returns (ShareProfitOutput memory outputs) {
        StorageStruct storage Storage = PositionManagerStorage.getStorage();
        uint24[] memory allowableFeeTiers = Storage.registry.getAllowableFeeTiers();

        uint256 amount0PerformanceFee;
        uint256 amount1PerformanceFee;
        {
            uint256 token0UsdValue;
            uint256 token0UsdPrice;
            if (inputs.token0 != Storage.registry.usdValueTokenAddress()) {
                address token0UsdValueTokenDeepestPool = UniswapHelper._findV3DeepestPool(
                    Storage.uniswapAddressHolder.uniswapV3FactoryAddress(),
                    inputs.token0,
                    Storage.registry.usdValueTokenAddress(),
                    allowableFeeTiers
                );

                (uint160 sqrtPriceX96, , , , , , ) = IUniswapV3Pool(token0UsdValueTokenDeepestPool).slot0();
                token0UsdValue = UniswapQuoter.quoteWithSqrtPrice(
                    inputs.token0,
                    Storage.registry.usdValueTokenAddress(),
                    inputs.amount0,
                    sqrtPriceX96
                );

                token0UsdPrice = SwapHelper.getPrice(
                    sqrtPriceX96,
                    inputs.token0,
                    Storage.registry.usdValueTokenAddress()
                );
            } else {
                token0UsdValue = inputs.amount0;
                token0UsdPrice = SwapHelper.getPriceWithSameToken(inputs.token0);
            }

            uint256 token1UsdValue;
            uint256 token1UsdPrice;
            if (inputs.token1 != Storage.registry.usdValueTokenAddress()) {
                address token1UsdValueTokenDeepestPool = UniswapHelper._findV3DeepestPool(
                    Storage.uniswapAddressHolder.uniswapV3FactoryAddress(),
                    inputs.token1,
                    Storage.registry.usdValueTokenAddress(),
                    allowableFeeTiers
                );

                (uint160 sqrtPriceX96, , , , , , ) = IUniswapV3Pool(token1UsdValueTokenDeepestPool).slot0();

                token1UsdValue = UniswapQuoter.quoteWithSqrtPrice(
                    inputs.token1,
                    Storage.registry.usdValueTokenAddress(),
                    inputs.amount1,
                    sqrtPriceX96
                );

                token1UsdPrice = SwapHelper.getPrice(
                    sqrtPriceX96,
                    inputs.token1,
                    Storage.registry.usdValueTokenAddress()
                );
            } else {
                token1UsdValue = inputs.amount1;
                token1UsdPrice = SwapHelper.getPriceWithSameToken(inputs.token1);
            }

            if (token0UsdValue.add(token1UsdValue) > inputs.originalDepositUsdValue) {
                uint256 performanceFeeUsd = _calPerformanceFee(
                    token0UsdValue.add(token1UsdValue).sub(inputs.originalDepositUsdValue),
                    inputs.performanceFeeRatio
                );

                (amount0PerformanceFee, amount1PerformanceFee) = SwapHelper.distributeTargetAmount(
                    inputs.token0,
                    inputs.token1,
                    inputs.amount0,
                    inputs.amount1,
                    token0UsdPrice,
                    token1UsdPrice,
                    performanceFeeUsd
                );
            }
        }

        {
            uint256 amount0PerformanceFeeDistributed;
            uint256 amount1PerformanceFeeDistributed;

            (amount0PerformanceFeeDistributed, amount1PerformanceFeeDistributed) = _determineTokensReceived(
                inputs.token0,
                inputs.token1,
                inputs.performanceFeeReceivedToken,
                amount0PerformanceFee,
                amount1PerformanceFee,
                allowableFeeTiers
            );

            (outputs.amount0Returned, outputs.amount1Returned) = _determineTokensReceived(
                inputs.token0,
                inputs.token1,
                inputs.returnedToken,
                inputs.amount0.sub(amount0PerformanceFee),
                inputs.amount1.sub(amount1PerformanceFee),
                allowableFeeTiers
            );

            _ShareResult memory result = _share(
                _ShareParams(
                    inputs.token0,
                    inputs.token1,
                    inputs.performanceFeeRecipient,
                    amount0PerformanceFeeDistributed,
                    amount1PerformanceFeeDistributed,
                    outputs.amount0Returned,
                    outputs.amount1Returned,
                    inputs.serviceFeeRatio
                )
            );

            emit ProfitShared(
                address(this),
                inputs.performanceFeeRecipient,
                result.performanceFeeAmount0,
                result.performanceFeeAmount1,
                result.serviceFeeAmount0,
                result.serviceFeeAmount1,
                result.returnedAmount0,
                result.returnedAmount1
            );
        }
    }

    function _calPerformanceFee(uint256 amount, uint24 ratio) internal pure returns (uint256) {
        uint256 MAX_RATIO = 10_000;
        return FullMath.mulDiv(amount, ratio, MAX_RATIO);
    }

    function _determineTokensReceived(
        address token0,
        address token1,
        address receivedToken,
        uint256 amount0,
        uint256 amount1,
        uint24[] memory allowableFeeTiers
    ) internal returns (uint256 amount0Received, uint256 amount1Received) {
        StorageStruct storage Storage = PositionManagerStorage.getStorage();

        address deepestPool = UniswapHelper._findV3DeepestPool(
            Storage.uniswapAddressHolder.uniswapV3FactoryAddress(),
            token0,
            token1,
            allowableFeeTiers
        );

        if (receivedToken == token0) {
            if (amount1 == 0) {
                amount0Received = amount0;
            } else {
                amount0Received = amount0.add(_swap(deepestPool, token1, receivedToken, amount1));
            }
        } else if (receivedToken == token1) {
            if (amount0 == 0) {
                amount1Received = amount1;
            } else {
                amount1Received = amount1.add(_swap(deepestPool, token0, receivedToken, amount0));
            }
        } else {
            amount0Received = amount0;
            amount1Received = amount1;
        }
    }

    function _calServiceFee(
        uint256 performanceFee,
        uint32 serviceFeeDenominator,
        uint32 serviceFeeRatio
    ) internal pure returns (uint256 serviceFee) {
        if (performanceFee > 0) {
            serviceFee = FullMath.mulDiv(performanceFee, serviceFeeRatio, serviceFeeDenominator);
        }
    }

    function _share(_ShareParams memory params) internal returns (_ShareResult memory result) {
        StorageStruct storage Storage = PositionManagerStorage.getStorage();

        uint256 amount0ServiceFee;
        uint256 amount1ServiceFee;
        if (params.amount0PerformanceFee > 0) {
            amount0ServiceFee = _calServiceFee(
                params.amount0PerformanceFee,
                Storage.registry.serviceFeeDenominator(),
                params.serviceFeeRatio
            );
            if (amount0ServiceFee > 0) {
                result.serviceFeeAmount0 = amount0ServiceFee;
                IERC20(params.token0).safeTransfer(Storage.registry.serviceFeeRecipient(), amount0ServiceFee);
            }

            if (params.amount0PerformanceFee > amount0ServiceFee) {
                result.performanceFeeAmount0 = params.amount0PerformanceFee.sub(amount0ServiceFee);
                IERC20(params.token0).safeTransfer(
                    params.performanceFeeRecipient,
                    params.amount0PerformanceFee.sub(amount0ServiceFee)
                );
            }
        }
        if (params.amount1PerformanceFee > 0) {
            amount1ServiceFee = _calServiceFee(
                params.amount1PerformanceFee,
                Storage.registry.serviceFeeDenominator(),
                params.serviceFeeRatio
            );
            if (amount1ServiceFee > 0) {
                result.serviceFeeAmount1 = amount1ServiceFee;
                IERC20(params.token1).safeTransfer(Storage.registry.serviceFeeRecipient(), amount1ServiceFee);
            }
            if (params.amount1PerformanceFee > amount1ServiceFee) {
                result.performanceFeeAmount1 = params.amount1PerformanceFee.sub(amount1ServiceFee);
                IERC20(params.token1).safeTransfer(
                    params.performanceFeeRecipient,
                    params.amount1PerformanceFee.sub(amount1ServiceFee)
                );
            }
        }

        if (params.amount0Returned > 0) {
            result.returnedAmount0 = params.amount0Returned;
            IERC20(params.token0).safeTransfer(Storage.owner, params.amount0Returned);
        }

        if (params.amount1Returned > 0) {
            result.returnedAmount1 = params.amount1Returned;
            IERC20(params.token1).safeTransfer(Storage.owner, params.amount1Returned);
        }
    }

    function _swap(
        address deepestPool,
        address tokenIn,
        address tokenOut,
        uint256 amountIn
    ) internal returns (uint256 amountOut) {
        StorageStruct storage Storage = PositionManagerStorage.getStorage();

        SwapHelper.checkDeviation(
            IUniswapV3Pool(deepestPool),
            Storage.registry.maxTwapDeviation(),
            Storage.registry.twapDuration()
        );

        ERC20Helper._approveToken(tokenIn, Storage.uniswapAddressHolder.swapRouterAddress(), amountIn);

        amountOut = ISwapRouter(Storage.uniswapAddressHolder.swapRouterAddress()).exactInputSingle(
            ISwapRouter.ExactInputSingleParams({
                tokenIn: tokenIn,
                tokenOut: tokenOut,
                fee: IUniswapV3Pool(deepestPool).fee(),
                recipient: address(this),
                deadline: block.timestamp,
                amountIn: amountIn,
                // TODO: slippage protection by using amountOutMinimum, sqrtPriceLimitX96
                amountOutMinimum: 0,
                sqrtPriceLimitX96: 0
            })
        );
    }
}

contracts/actions/SingleTokenIncreaseLiq.content:
// SPDX-License-Identifier: GPL-2.0

pragma solidity 0.7.6;
pragma abicoder v2;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/SafeERC20.sol";
import "@openzeppelin/contracts/math/SafeMath.sol";
import "@uniswap/v3-periphery/contracts/interfaces/INonfungiblePositionManager.sol";
import "@uniswap/v3-periphery/contracts/interfaces/ISwapRouter.sol";
import "../libraries/ERC20Helper.sol";
import "../libraries/UniswapHelper.sol";
import "../libraries/SwapHelper.sol";
import "../Storage.sol";
import "../interfaces/actions/ISingleTokenIncreaseLiquidity.sol";

///@notice action to increase the liquidity of a V3 position
contract SingleTokenIncreaseLiquidity is ISingleTokenIncreaseLiquidity {
    using SafeERC20 for IERC20;
    using SafeMath for uint256;

    ///@notice emitted when liquidity is increased with a single token
    ///@param positionManager address of the position manager which increased liquidity
    ///@param tokenId id of the position
    ///@param tokenIn address of token zapped in
    ///@param amountIn amount of tokenIn zapped in
    ///@param amount0Increased amount of token0 increased
    ///@param amount1Increased amount of token1 increased
    ///@param amount0Leftover amount of token0 leftover
    ///@param amount1Leftover amount of token1 leftover
    event LiquidityIncreasedWithSingleToken(
        address indexed positionManager,
        uint256 tokenId,
        address tokenIn,
        uint256 amountIn,
        uint256 amount0Increased,
        uint256 amount1Increased,
        uint256 amount0Leftover,
        uint256 amount1Leftover
    );

    ///@notice increase the liquidity of a UniswapV3 position
    ///@param inputs struct of SingleTokenIncreaseLiquidityInput parameters
    ///@return outputs struct of SingleTokenIncreaseLiquidityOutput parameters
    function singleTokenIncreaseLiquidity(
        SingleTokenIncreaseLiquidityInput calldata inputs
    ) external override returns (SingleTokenIncreaseLiquidityOutput memory outputs) {
        require(inputs.amountIn != 0, "ILA");

        StorageStruct storage Storage = PositionManagerStorage.getStorage();
        uint24[] memory allowableFeeTiers = Storage.registry.getAllowableFeeTiers();

        uint256 amountToSwap;
        IUniswapV3Pool deepestPool;
        {
            deepestPool = IUniswapV3Pool(
                UniswapHelper._findV3DeepestPool(
                    Storage.uniswapAddressHolder.uniswapV3FactoryAddress(),
                    inputs.token0,
                    inputs.token1,
                    allowableFeeTiers
                )
            );

            IUniswapV3Pool depositPool = IUniswapV3Pool(
                UniswapHelper._getPool(
                    Storage.uniswapAddressHolder.uniswapV3FactoryAddress(),
                    inputs.token0,
                    inputs.token1,
                    inputs.fee
                )
            );

            (uint160 sqrtPriceX96, , , , , , ) = deepestPool.slot0();
            (uint160 sqrtRatioX96, , , , , , ) = depositPool.slot0();

            (amountToSwap, ) = SwapHelper.calcAmountToSwap(
                sqrtRatioX96,
                inputs.tickLower,
                inputs.tickUpper,
                sqrtPriceX96,
                inputs.isToken0In ? inputs.amountIn : 0,
                inputs.isToken0In ? 0 : inputs.amountIn
            );
        }

        uint256 amountOut;
        if (amountToSwap != 0) {
            amountOut = _swap(address(deepestPool), inputs.token0, inputs.token1, inputs.isToken0In, amountToSwap);
        }

        (
            outputs.amount0Increased,
            outputs.amount1Increased,
            outputs.amount0Leftover,
            outputs.amount1Leftover
        ) = _increaseLiquidity(
            inputs.tokenId,
            inputs.token0,
            inputs.token1,
            inputs.isToken0In ? inputs.amountIn.sub(amountToSwap) : amountOut,
            inputs.isToken0In ? amountOut : inputs.amountIn.sub(amountToSwap)
        );

        emit LiquidityIncreasedWithSingleToken(
            address(this),
            inputs.tokenId,
            inputs.isToken0In ? inputs.token0 : inputs.token1,
            inputs.amountIn,
            outputs.amount0Increased,
            outputs.amount1Increased,
            outputs.amount0Leftover,
            outputs.amount1Leftover
        );
    }

    function _swap(
        address deepestPool,
        address token0,
        address token1,
        bool isToken0In,
        uint256 amountToSwap
    ) internal returns (uint256 amountOut) {
        StorageStruct storage Storage = PositionManagerStorage.getStorage();

        SwapHelper.checkDeviation(
            IUniswapV3Pool(deepestPool),
            Storage.registry.maxTwapDeviation(),
            Storage.registry.twapDuration()
        );

        ERC20Helper._approveToken(
            isToken0In ? token0 : token1,
            Storage.uniswapAddressHolder.swapRouterAddress(),
            amountToSwap
        );

        amountOut = ISwapRouter(Storage.uniswapAddressHolder.swapRouterAddress()).exactInputSingle(
            ISwapRouter.ExactInputSingleParams({
                tokenIn: isToken0In ? token0 : token1,
                tokenOut: isToken0In ? token1 : token0,
                fee: IUniswapV3Pool(deepestPool).fee(),
                recipient: address(this),
                deadline: block.timestamp,
                amountIn: amountToSwap,
                // TODO: slippage protection by using amountOutMinimum, sqrtPriceLimitX96
                amountOutMinimum: 0,
                sqrtPriceLimitX96: 0
            })
        );
    }

    function _increaseLiquidity(
        uint256 tokenId,
        address token0Address,
        address token1Address,
        uint256 amount0Desired,
        uint256 amount1Desired
    )
        internal
        returns (uint256 amount0Increased, uint256 amount1Increased, uint256 amount0Leftover, uint256 amount1Leftover)
    {
        StorageStruct storage Storage = PositionManagerStorage.getStorage();

        address nonfungiblePositionManagerAddress = Storage.uniswapAddressHolder.nonfungiblePositionManagerAddress();

        ERC20Helper._approveToken(token0Address, nonfungiblePositionManagerAddress, amount0Desired);
        ERC20Helper._approveToken(token1Address, nonfungiblePositionManagerAddress, amount1Desired);

        INonfungiblePositionManager.IncreaseLiquidityParams memory params = INonfungiblePositionManager
            .IncreaseLiquidityParams({
                tokenId: tokenId,
                amount0Desired: amount0Desired,
                amount1Desired: amount1Desired,
                amount0Min: 0,
                amount1Min: 0,
                deadline: block.timestamp
            });
        (, amount0Increased, amount1Increased) = INonfungiblePositionManager(nonfungiblePositionManagerAddress)
            .increaseLiquidity(params);

        amount0Leftover = amount0Desired.sub(amount0Increased);
        amount1Leftover = amount1Desired.sub(amount1Increased);
    }
}

contracts/actions/SwapToPositionRatio.so.content:
// SPDX-License-Identifier: GPL-2.0

pragma solidity 0.7.6;
pragma abicoder v2;

import "@uniswap/v3-periphery/contracts/interfaces/ISwapRouter.sol";
import "@openzeppelin/contracts/math/SafeMath.sol";
import "../libraries/SwapHelper.sol";
import "../libraries/UniswapHelper.sol";
import "../libraries/ERC20Helper.sol";
import "../Storage.sol";
import "../interfaces/actions/ISwapToPositionRatio.sol";

///@notice action to swap to an exact position ratio
contract SwapToPositionRatio is ISwapToPositionRatio {
    using SafeMath for uint256;

    ///@notice emitted when a positionManager swaps to ratio
    ///@param positionManager address of PositionManager
    ///@param token0 address of first token of the pool
    ///@param token1 address of second token of the pool
    ///@param amount0Out token0 amount swapped
    ///@param amount1Out token1 amount swapped
    event SwappedToPositionRatio(
        address indexed positionManager,
        address token0,
        address token1,
        uint256 amount0Out,
        uint256 amount1Out
    );

    ///@notice performs swap to optimal ratio for the position at tickLower and tickUpper
    ///@param inputs struct containing the inputs for the swap
    ///@return amount0Out the new value of amount0
    ///@return amount1Out the new value of amount1
    function swapToPositionRatio(
        SwapToPositionInput memory inputs
    ) external override returns (uint256 amount0Out, uint256 amount1Out) {
        StorageStruct storage Storage = PositionManagerStorage.getStorage();
        uint24[] memory allowableFeeTiers = Storage.registry.getAllowableFeeTiers();

        {
            IUniswapV3Pool deepestPool = IUniswapV3Pool(
                UniswapHelper._findV3DeepestPool(
                    Storage.uniswapAddressHolder.uniswapV3FactoryAddress(),
                    inputs.token0Address,
                    inputs.token1Address,
                    allowableFeeTiers
                )
            );
            IUniswapV3Pool pool = IUniswapV3Pool(
                UniswapHelper._getPool(
                    Storage.uniswapAddressHolder.uniswapV3FactoryAddress(),
                    inputs.token0Address,
                    inputs.token1Address,
                    inputs.fee
                )
            );

            (amount0Out, amount1Out) = getAmount0OutAmount1Out(
                pool,
                deepestPool,
                inputs.token0Address,
                inputs.token1Address,
                inputs.amount0In,
                inputs.amount1In,
                inputs.tickLower,
                inputs.tickUpper
            );
        }

        emit SwappedToPositionRatio(address(this), inputs.token0Address, inputs.token1Address, amount0Out, amount1Out);
    }

    function getAmount0OutAmount1Out(
        IUniswapV3Pool pool,
        IUniswapV3Pool deepestPool,
        address token0,
        address token1,
        uint256 amount0In,
        uint256 amount1In,
        int24 tickLower,
        int24 tickUpper
    ) internal returns (uint256 amount0Out, uint256 amount1Out) {
        (uint160 sqrtRatioX96, , , , , , ) = pool.slot0();
        (uint160 sqrtPriceX96, , , , , , ) = deepestPool.slot0();

        (uint256 amountToSwap, bool isToken0In) = SwapHelper.calcAmountToSwap(
            sqrtRatioX96,
            tickLower,
            tickUpper,
            sqrtPriceX96,
            amount0In,
            amount1In
        );

        if (amountToSwap != 0) {
            uint256 amountSwapped = _swap(
                address(deepestPool),
                isToken0In ? token0 : token1,
                isToken0In ? token1 : token0,
                amountToSwap
            );

            ///@notice return the new amount of the token swapped and the token returned
            ///@dev token0AddressIn true amount 0 - amountToSwap  ------ amount 1 + amountSwapped
            ///@dev token0AddressIn false amount 0 + amountSwapped  ------ amount 1 - amountToSwap
            amount0Out = isToken0In ? amount0In.sub(amountToSwap) : amount0In.add(amountSwapped);
            amount1Out = isToken0In ? amount1In.add(amountSwapped) : amount1In.sub(amountToSwap);
        } else {
            amount0Out = amount0In;
            amount1Out = amount1In;
        }
    }

    ///@notice performs a swap
    ///@param deepestPool address of deepest pool
    ///@param tokenIn address of input token
    ///@param tokenOut address of output
    ///@param amountIn amount of tokenIn to swap
    function _swap(
        address deepestPool,
        address tokenIn,
        address tokenOut,
        uint256 amountIn
    ) internal returns (uint256 amountOut) {
        StorageStruct storage Storage = PositionManagerStorage.getStorage();

        SwapHelper.checkDeviation(
            IUniswapV3Pool(deepestPool),
            Storage.registry.maxTwapDeviation(),
            Storage.registry.twapDuration()
        );

        ERC20Helper._approveToken(tokenIn, Storage.uniswapAddressHolder.swapRouterAddress(), amountIn);

        amountOut = ISwapRouter(Storage.uniswapAddressHolder.swapRouterAddress()).exactInputSingle(
            ISwapRouter.ExactInputSingleParams({
                tokenIn: tokenIn,
                tokenOut: tokenOut,
                fee: IUniswapV3Pool(deepestPool).fee(),
                recipient: address(this),
                deadline: block.timestamp,
                amountIn: amountIn,
                // TODO: slippage protection by using amountOutMinimum, sqrtPriceLimitX96
                amountOutMinimum: 0,
                sqrtPriceLimitX96: 0
            })
        );
    }
}

contracts/actions/ZapIn.sol.content:
// SPDX-License-Identifier: GPL-2.0

pragma solidity 0.7.6;
pragma abicoder v2;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/SafeERC20.sol";
import "@openzeppelin/contracts/math/SafeMath.sol";
import "@uniswap/v3-periphery/contracts/interfaces/INonfungiblePositionManager.sol";
import "@uniswap/v3-periphery/contracts/interfaces/ISwapRouter.sol";
import "@uniswap/v3-core/contracts/interfaces/IUniswapV3Pool.sol";
import "../libraries/UniswapHelper.sol";
import "../libraries/SwapHelper.sol";
import "../libraries/ERC20Helper.sol";
import "../Storage.sol";
import "../interfaces/IPositionManager.sol";
import "../interfaces/actions/IZapIn.sol";

contract ZapIn is IZapIn {
    using SafeERC20 for IERC20;
    using SafeMath for uint256;

    ///@notice emitted when a UniswapNFT is zapped in
    ///@param positionManager address of PositionManager
    ///@param tokenId Id of zapped token
    ///@param tokenIn address of token zapped in
    ///@param amountIn amount of tokenIn zapped in
    ///@param amount0Deposited token0 amount deposited
    ///@param amount1Deposited token1 amount deposited
    ///@param amount0Leftover token0 amount leftover
    ///@param amount1Leftover token1 amount leftover
    event ZappedIn(
        address indexed positionManager,
        uint256 tokenId,
        address tokenIn,
        uint256 amountIn,
        uint256 amount0Deposited,
        uint256 amount1Deposited,
        uint256 amount0Leftover,
        uint256 amount1Leftover
    );

    ///@notice mints a uni NFT with a single input token, the token in input must be one of the two token in the pool
    ///@param inputs struct of ZapInInput parameters
    ///@return outputs struct of ZapInOutput parameters
    function zapIn(ZapInInput calldata inputs) external override returns (ZapInOutput memory outputs) {
        StorageStruct storage Storage = PositionManagerStorage.getStorage();
        uint24[] memory allowableFeeTiers = Storage.registry.getAllowableFeeTiers();

        uint256 amountToSwap;
        {
            IUniswapV3Pool depositPool = IUniswapV3Pool(
                UniswapHelper._getPool(
                    Storage.uniswapAddressHolder.uniswapV3FactoryAddress(),
                    inputs.token0,
                    inputs.token1,
                    inputs.fee
                )
            );

            IUniswapV3Pool deepestPool = IUniswapV3Pool(
                UniswapHelper._findV3DeepestPool(
                    Storage.uniswapAddressHolder.uniswapV3FactoryAddress(),
                    inputs.token0,
                    inputs.token1,
                    allowableFeeTiers
                )
            );

            (amountToSwap, ) = SwapHelper.calcAmountToSwap(
                getSqrtRatioX96(depositPool),
                inputs.tickLower,
                inputs.tickUpper,
                getSqrtPriceX96(deepestPool),
                inputs.isToken0In ? inputs.amountIn : 0,
                inputs.isToken0In ? 0 : inputs.amountIn
            );
        }

        uint256 amount0Desired;
        uint256 amount1Desired;
        {
            IUniswapV3Pool deepestPool = IUniswapV3Pool(
                UniswapHelper._findV3DeepestPool(
                    Storage.uniswapAddressHolder.uniswapV3FactoryAddress(),
                    inputs.token0,
                    inputs.token1,
                    allowableFeeTiers
                )
            );
            (amount0Desired, amount1Desired) = _getDesiredAmounts(
                deepestPool,
                inputs.token0,
                inputs.token1,
                inputs.amountIn,
                inputs.isToken0In,
                amountToSwap
            );
        }

        (outputs.tokenId, outputs.amount0Deposited, outputs.amount1Deposited) = _mint(
            inputs.token0,
            inputs.token1,
            inputs.fee,
            inputs.tickLower,
            inputs.tickUpper,
            amount0Desired,
            amount1Desired
        );

        outputs.amount0Leftover = amount0Desired.sub(outputs.amount0Deposited);
        outputs.amount1Leftover = amount1Desired.sub(outputs.amount1Deposited);

        emit ZappedIn(
            address(this),
            outputs.tokenId,
            inputs.isToken0In ? inputs.token0 : inputs.token1,
            inputs.amountIn,
            outputs.amount0Deposited,
            outputs.amount1Deposited,
            outputs.amount0Leftover,
            outputs.amount1Leftover
        );
    }

    function getSqrtPriceX96(IUniswapV3Pool pool) internal view returns (uint160 sqrtPriceX96) {
        (sqrtPriceX96, , , , , , ) = pool.slot0();
    }

    function getSqrtRatioX96(IUniswapV3Pool pool) internal view returns (uint160 sqrtRatioX96) {
        (sqrtRatioX96, , , , , , ) = pool.slot0();
    }

    function _getDesiredAmounts(
        IUniswapV3Pool deepestPool,
        address token0,
        address token1,
        uint256 amountIn,
        bool isToken0In,
        uint256 amountToSwap
    ) internal returns (uint256 amount0Desired, uint256 amount1Desired) {
        StorageStruct storage Storage = PositionManagerStorage.getStorage();

        uint256 amountOut;
        if (amountToSwap != 0) {
            SwapHelper.checkDeviation(
                deepestPool,
                Storage.registry.maxTwapDeviation(),
                Storage.registry.twapDuration()
            );

            ERC20Helper._approveToken(
                isToken0In ? token0 : token1,
                Storage.uniswapAddressHolder.swapRouterAddress(),
                amountToSwap
            );

            amountOut = ISwapRouter(Storage.uniswapAddressHolder.swapRouterAddress()).exactInputSingle(
                ISwapRouter.ExactInputSingleParams({
                    tokenIn: isToken0In ? token0 : token1,
                    tokenOut: isToken0In ? token1 : token0,
                    fee: deepestPool.fee(),
                    recipient: address(this),
                    deadline: block.timestamp,
                    amountIn: amountToSwap,
                    // TODO: slippage protection by using amountOutMinimum, sqrtPriceLimitX96
                    amountOutMinimum: 0,
                    sqrtPriceLimitX96: 0
                })
            );
        }

        amount0Desired = isToken0In ? amountIn.sub(amountToSwap) : amountOut;
        amount1Desired = isToken0In ? amountOut : amountIn.sub(amountToSwap);
    }

    ///@notice mints a UniswapV3 position NFT
    ///@param token0Address address of the first token
    ///@param token1Address address of the second token
    ///@param fee pool fee level
    ///@param tickLower lower tick of the position
    ///@param tickUpper upper tick of the position
    ///@param amount0Desired amount of first token in position
    ///@param amount1Desired amount of second token in position
    function _mint(
        address token0Address,
        address token1Address,
        uint24 fee,
        int24 tickLower,
        int24 tickUpper,
        uint256 amount0Desired,
        uint256 amount1Desired
    ) internal returns (uint256 tokenId, uint256 amount0Deposited, uint256 amount1Deposited) {
        StorageStruct storage Storage = PositionManagerStorage.getStorage();

        address nonfungiblePositionManagerAddress = Storage.uniswapAddressHolder.nonfungiblePositionManagerAddress();

        ERC20Helper._approveToken(token0Address, nonfungiblePositionManagerAddress, amount0Desired);
        ERC20Helper._approveToken(token1Address, nonfungiblePositionManagerAddress, amount1Desired);

        INonfungiblePositionManager.MintParams memory params = INonfungiblePositionManager.MintParams({
            token0: token0Address,
            token1: token1Address,
            fee: fee,
            tickLower: tickLower,
            tickUpper: tickUpper,
            amount0Desired: amount0Desired,
            amount1Desired: amount1Desired,
            amount0Min: 0,
            amount1Min: 0,
            recipient: address(this),
            deadline: block.timestamp
        });

        (tokenId, , amount0Deposited, amount1Deposited) = INonfungiblePositionManager(nonfungiblePositionManagerAddress)
            .mint(params);
    }
}

