// SPDX-License-Identifier: GPL-2.0

pragma solidity 0.7.6;
pragma abicoder v2;

import "../interfaces/IERC20Extended.sol";
import "@openzeppelin/contracts/math/SafeMath.sol";
import "./SwapHelper.sol";

library UniswapQuoter {
    using SafeMath for uint256;

    function quoteWithSqrtPrice(
        address tokenIn,
        address tokenOut,
        uint256 amountIn,
        uint160 sqrtPriceX96
    ) internal view returns (uint256) {
        if (amountIn == 0) {
            return 0;
        }

        if (tokenIn == tokenOut) {
            return amountIn;
        }

        bool isReorder = tokenIn > tokenOut;
        (address _token0, address _token1) = isReorder ? (tokenOut, tokenIn) : (tokenIn, tokenOut);

        uint8 token0Decimals = IERC20Extended(_token0).decimals();
        uint8 token1Decimals = IERC20Extended(_token1).decimals();

        (uint256 token0AsBasePrice, uint256 token1AsBasePrice) = SwapHelper.getPriceFromSqrtPriceX96(
            sqrtPriceX96,
            token0Decimals,
            token1Decimals
        );

        uint256 price = isReorder ? token1AsBasePrice : token0AsBasePrice;
        uint8 decimals = isReorder ? token1Decimals : token0Decimals;

        return FullMath.mulDiv(amountIn, price, uint256(10 ** decimals));
    }
}

