// SPDX-License-Identifier: GPL-2.0

pragma solidity 0.7.6;
pragma abicoder v2;

import "@uniswap/v3-periphery/contracts/interfaces/INonfungiblePositionManager.sol";
import "@uniswap/v3-core/contracts/interfaces/IUniswapV3Pool.sol";
import "@openzeppelin/contracts/math/SafeMath.sol";
import "./BaseModule.sol";
import "../libraries/SafeInt24Math.sol";
import "../libraries/UniswapHelper.sol";
import "../libraries/MathHelper.sol";
import "../interfaces/modules/IIdleLiquidityModule.sol";
import "../interfaces/IPositionManagerFactory.sol";
import "../interfaces/IPositionManager.sol";
import "../interfaces/IUniswapAddressHolder.sol";
import "../interfaces/actions/IClosePosition.sol";
import "../interfaces/actions/IRepayRebalanceFee.sol";
import "../interfaces/actions/ISwapToPositionRatio.sol";
import "../interfaces/actions/IMint.sol";

///@title Idle Liquidity Module to manage liquidity for a user position
contract IdleLiquidityModule is BaseModule, IIdleLiquidityModule {
    ///@notice uniswap address holder
    IUniswapAddressHolder public immutable uniswapAddressHolder;
    using SafeMath for uint256;
    using SafeInt24Math for int24;

    ///@notice emitted when a position is rebalanced
    ///@param positionManager address of the called position manager
    ///@param closedTokenId closed tokenId
    ///@param mintedTokenId minted tokenId
    ///@param collectedFee0 amount of token0 collected
    ///@param collectedFee1 amount of token1 collected
    event positionRebalanced(
        address indexed positionManager,
        uint256 closedTokenId,
        uint256 mintedTokenId,
        uint256 collectedFee0,
        uint256 collectedFee1
    );

    ///@notice assing the uniswap address holder to the contract
    ///@param _registry address of the registry
    ///@param _uniswapAddressHolder address of the uniswap address holder
    constructor(address _registry, address _uniswapAddressHolder) BaseModule(_registry) {
        require(_uniswapAddressHolder != address(0), "ILCA0");
        require(_registry != address(0), "ILCA0");

        uniswapAddressHolder = IUniswapAddressHolder(_uniswapAddressHolder);
    }

    ///@notice check if the position is out of range and rebalance it by swapping the tokens as necessary
    ///@param input RebalanceInput struct
    function rebalance(RebalanceInput calldata input) external onlyWhitelistedKeeper {
        address positionManager = IPositionManagerFactory(registry.positionManagerFactoryAddress())
            .userToPositionManager(input.userAddress);
        require(positionManager != address(0), "ILPM0");

        IPositionManager.PositionInfo memory pInfo = IPositionManager(positionManager).getPositionInfo(
            input.positionId
        );

        _checkCurrentTickOutOfRange(pInfo.tokenId);

        uint256 amount0CollectedFee;
        uint256 amount1CollectedFee;
        uint256 newTokenId;

        {
            uint256 amount0Removed;
            uint256 amount1Removed;

            (amount0CollectedFee, amount1CollectedFee, amount0Removed, amount1Removed) = _closedAndRepayRebalance(
                _CloseAndRepayRebalanceParams(positionManager, pInfo.tokenId, input.estimatedGasFee, input.isForced)
            );

            require(amount0Removed > 0 || amount1Removed > 0, "ILAR");

            uint256 amount0Leftover;
            uint256 amount1Leftover;
            (newTokenId, amount0Leftover, amount1Leftover) = _swapAndMint(
                _SwapAndMintParams(
                    positionManager,
                    pInfo.tokenId,
                    amount0Removed.add(amount0CollectedFee).add(pInfo.amount0Leftover),
                    amount1Removed.add(amount1CollectedFee).add(pInfo.amount1Leftover),
                    pInfo.tickLowerDiff,
                    pInfo.tickLowerDiff
                )
            );

            pInfo.amount0CollectedFee = pInfo.amount0CollectedFee.add(amount0CollectedFee);
            pInfo.amount1CollectedFee = pInfo.amount1CollectedFee.add(amount1CollectedFee);

            IPositionManager(positionManager).middlewareRebalance(
                input.positionId,
                newTokenId,
                pInfo.tickLowerDiff,
                pInfo.tickUpperDiff,
                pInfo.amount0CollectedFee,
                pInfo.amount1CollectedFee,
                amount0Leftover,
                amount1Leftover
            );
        }

        emit positionRebalanced(positionManager, pInfo.tokenId, newTokenId, amount0CollectedFee, amount1CollectedFee);
    }

    ///@notice check if the position is out of range and rebalance it by swapping the tokens as necessary and customize lower & upper tick diff
    ///@param input RebalanceWithTickDiffsInput struct
    function rebalanceWithTickDiffs(RebalanceWithTickDiffsInput calldata input) external onlyWhitelistedKeeper {
        require(input.tickLowerDiff <= 0 && input.tickUpperDiff >= 0, "ILTD");

        address positionManager = IPositionManagerFactory(registry.positionManagerFactoryAddress())
            .userToPositionManager(input.userAddress);
        require(positionManager != address(0), "ILPM0");

        IPositionManager.PositionInfo memory pInfo = IPositionManager(positionManager).getPositionInfo(
            input.positionId
        );

        _checkCurrentTickOutOfRange(pInfo.tokenId);

        uint256 amount0CollectedFee;
        uint256 amount1CollectedFee;
        uint256 newTokenId;

        {
            uint256 amount0Removed;
            uint256 amount1Removed;
            (amount0CollectedFee, amount1CollectedFee, amount0Removed, amount1Removed) = _closedAndRepayRebalance(
                _CloseAndRepayRebalanceParams(positionManager, pInfo.tokenId, input.estimatedGasFee, input.isForced)
            );

            require(amount0Removed > 0 || amount1Removed > 0, "ILAR");

            uint256 amount0Leftover;
            uint256 amount1Leftover;
            (newTokenId, amount0Leftover, amount1Leftover) = _swapAndMint(
                _SwapAndMintParams(
                    positionManager,
                    pInfo.tokenId,
                    amount0Removed.add(amount0CollectedFee).add(pInfo.amount0Leftover),
                    amount1Removed.add(amount1CollectedFee).add(pInfo.amount1Leftover),
                    input.tickLowerDiff,
                    input.tickUpperDiff
                )
            );

            pInfo.amount0CollectedFee = pInfo.amount0CollectedFee.add(amount0CollectedFee);
            pInfo.amount1CollectedFee = pInfo.amount1CollectedFee.add(amount1CollectedFee);

            IPositionManager(positionManager).middlewareRebalance(
                input.positionId,
                newTokenId,
                input.tickLowerDiff,
                input.tickUpperDiff,
                pInfo.amount0CollectedFee,
                pInfo.amount1CollectedFee,
                amount0Leftover,
                amount1Leftover
            );
        }

        emit positionRebalanced(positionManager, pInfo.tokenId, newTokenId, amount0CollectedFee, amount1CollectedFee);
    }

    ///@notice rebalances the position by swapping the tokens
    ///@param positionManager address of the position manager
    ///@param tokenId tokenId of the position
    ///@param tickLower new tickLower of the position
    ///@param tickUpper new tickUpper of the position
    struct _CloseAndRepayRebalanceParams {
        address positionManager;
        uint256 tokenId;
        uint256 rebalanceFee;
        bool isForced;
    }

    function _closedAndRepayRebalance(
        _CloseAndRepayRebalanceParams memory params
    )
        internal
        returns (
            uint256 amount0CollectedFee,
            uint256 amount1CollectedFee,
            uint256 amount0Removed,
            uint256 amount1Removed
        )
    {
        (address token0, address token1, , , ) = UniswapHelper._getTokens(
            params.tokenId,
            INonfungiblePositionManager(uniswapAddressHolder.nonfungiblePositionManagerAddress())
        );

        ///@dev call closePositionAction
        (amount0CollectedFee, amount1CollectedFee, amount0Removed, amount1Removed) = IClosePosition(
            params.positionManager
        ).closePosition(params.tokenId, false);

        ///@dev call repayRebalanceFeeAction
        (uint256 amount0Repay, uint256 amount1Repay, ) = IRepayRebalanceFee(params.positionManager).repayRebalanceFee(
            IRepayRebalanceFee.RepayRebalanceFeeInput({
                token0: token0,
                token1: token1,
                amount0UsedToRepay: params.isForced ? amount0CollectedFee.add(amount0Removed) : amount0CollectedFee,
                amount1UsedToRepay: params.isForced ? amount1CollectedFee.add(amount1Removed) : amount1CollectedFee,
                rebalanceFee: params.rebalanceFee,
                receiver: msg.sender
            })
        );

        if (amount0Repay > amount0CollectedFee) {
            amount0CollectedFee = 0;
            amount0Removed = amount0Removed.sub(amount0Repay.sub(amount0CollectedFee));
        } else {
            amount0CollectedFee = amount0CollectedFee.sub(amount0Repay);
        }

        if (amount1Repay > amount1CollectedFee) {
            amount1CollectedFee = 0;
            amount1Removed = amount1Removed.sub(amount1Repay.sub(amount1CollectedFee));
        } else {
            amount1CollectedFee = amount1CollectedFee.sub(amount1Repay);
        }
    }

    ///@notice rebalances the position by swapping the tokens
    ///@param positionManager address of the position manager
    ///@param tokenId tokenId of the position
    ///@param amount0 amount0 to be swapped and mint
    ///@param amount1 amount1 to be swapped and mint
    ///@param tickLowerDiff diff of tickLower of the position
    ///@param tickUpperDiff diff of tickUpper of the position
    struct _SwapAndMintParams {
        address positionManager;
        uint256 tokenId;
        uint256 amount0;
        uint256 amount1;
        int24 tickLowerDiff;
        int24 tickUpperDiff;
    }

    function _swapAndMint(
        _SwapAndMintParams memory params
    ) internal returns (uint256 newTokenId, uint256 amount0LeftOver, uint256 amount1LeftOver) {
        (address token0, address token1, uint24 fee, , ) = UniswapHelper._getTokens(
            params.tokenId,
            INonfungiblePositionManager(uniswapAddressHolder.nonfungiblePositionManagerAddress())
        );

        uint256 amount0Deposit;
        uint256 amount1Deposit;
        ///@dev call swapToPositionAction to perform the swap
        (uint256 amount0AfterSwapped, uint256 amount1AfterSwapped) = ISwapToPositionRatio(params.positionManager)
            .swapToPositionRatio(
                ISwapToPositionRatio.SwapToPositionInput({
                    token0Address: token0,
                    token1Address: token1,
                    fee: fee,
                    amount0In: params.amount0,
                    amount1In: params.amount1,
                    tickLower: _getCurrentTick(token0, token1, fee).add(params.tickLowerDiff),
                    tickUpper: _getCurrentTick(token0, token1, fee).add(params.tickUpperDiff)
                })
            );

        ///@dev call mintAction
        (newTokenId, amount0Deposit, amount1Deposit) = IMint(params.positionManager).mint(
            IMint.MintInput({
                token0Address: token0,
                token1Address: token1,
                fee: fee,
                tickLower: _getCurrentTick(token0, token1, fee).add(params.tickLowerDiff),
                tickUpper: _getCurrentTick(token0, token1, fee).add(params.tickUpperDiff),
                amount0Desired: amount0AfterSwapped,
                amount1Desired: amount1AfterSwapped,
                isReturnLeftOver: false
            })
        );

        ///@dev cal leftOver
        amount0LeftOver = amount0AfterSwapped.sub(amount0Deposit);
        amount1LeftOver = amount1AfterSwapped.sub(amount1Deposit);
    }

    function _checkCurrentTickOutOfRange(uint256 tokenId) internal view {
        (address token0, address token1, uint24 fee, int24 tickLower, int24 tickUpper) = UniswapHelper._getTokens(
            tokenId,
            INonfungiblePositionManager(uniswapAddressHolder.nonfungiblePositionManagerAddress())
        );

        int24 currentTick = _getCurrentTick(token0, token1, fee);

        require(currentTick < tickLower || currentTick > tickUpper, "ILOOR");
    }

    function _getCurrentTick(address token0, address token1, uint24 fee) internal view returns (int24 currentTick) {
        IUniswapV3Pool pool = IUniswapV3Pool(
            UniswapHelper._getPool(uniswapAddressHolder.uniswapV3FactoryAddress(), token0, token1, fee)
        );
        (, currentTick, , , , , ) = pool.slot0();
    }
}

contracts/PositionManager.sol.content:
// SPDX-License-Identifier: GPL-2.0

pragma solidity 0.7.6;
pragma abicoder v2;

import "@openzeppelin/contracts/token/ERC721/ERC721Holder.sol";
import "@openzeppelin/contracts/proxy/Initializable.sol";
import "@openzeppelin/contracts/math/SafeMath.sol";
import "@uniswap/v3-periphery/contracts/interfaces/INonfungiblePositionManager.sol";
import "./libraries/ERC20Helper.sol";
import "./Storage.sol";

/**
 * @title   Position Manager
 * @notice  A vault that provides liquidity on Uniswap V3.
 * @notice  User can Deposit here its Uni-v3 position
 * @notice  If user does so, he is sure that idle liquidity will always be employed in protocols
 * @notice  User will pay fee to external keepers
 * @notice  vault works for multiple positions
 */

contract PositionManager is IPositionManager, ERC721Holder, Initializable {
    using SafeMath for uint256;

    uint256[] private runningPositionIds;
    uint256[] private closedPositionIds;
    uint256 private positionIdCounter;
    uint256[] private tokenIds;

    mapping(uint256 => PositionInfo) public positions;

    mapping(uint256 => mapping(address => bytes32)) public positionToModuleData;

    ///@notice emitted when a ERC20 is withdrawn
    ///@param tokenAddress address of the ERC20
    ///@param to address of the user
    ///@param amount of the ERC20
    event ERC20Withdrawn(address tokenAddress, address to, uint256 amount);

    ///@notice modifier to check if the msg.sender is the owner
    modifier onlyOwner() {
        StorageStruct storage Storage = PositionManagerStorage.getStorage();
        require(msg.sender == Storage.owner, "PMOO");
        _;
    }

    ///@notice modifier to check if the msg.sender is the governance
    modifier onlyGovernance() {
        StorageStruct storage Storage = PositionManagerStorage.getStorage();
        require(msg.sender == Storage.registry.governance(), "PMOG");
        _;
    }

    ///@notice modifier to check if the msg.sender is whitelisted
    modifier onlyWhitelisted() {
        require(_calledFromActiveModule(msg.sender) || msg.sender == address(this), "PMOW");
        _;
    }

    ///@notice modifier to check if the msg.sender is the PositionManagerFactory
    modifier onlyFactory() {
        StorageStruct storage Storage = PositionManagerStorage.getStorage();
        require(Storage.registry.positionManagerFactoryAddress() == msg.sender, "PMOF");
        _;
    }

    ///@notice modifier to check if the position is owned by the positionManager
    modifier onlyOwnedPosition(uint256 tokenId) {
        StorageStruct storage Storage = PositionManagerStorage.getStorage();
        require(
            INonfungiblePositionManager(Storage.uniswapAddressHolder.nonfungiblePositionManagerAddress()).ownerOf(
                tokenId
            ) == address(this),
            "PMOOP"
        );
        _;
    }

    ///@notice modifier to check if the position exists
    modifier positionExists(uint256 positionId) {
        require(positions[positionId].tokenId != 0, "PMPE");
        _;
    }

    ///@notice modifier to check if the position is running
    modifier positionRunning(uint256 positionId) {
        require(_isPositionRunning(positionId), "PMPR");
        _;
    }

    constructor(address _owner, address _diamondCutFacet, address _registry) payable {
        PositionManagerStorage.setContractOwner(_owner);

        // Add the diamondCut external function from the diamondCutFacet
        IDiamondCut.FacetCut[] memory cut = new IDiamondCut.FacetCut[](1);
        bytes4[] memory functionSelectors = new bytes4[](1);
        functionSelectors[0] = IDiamondCut.diamondCut.selector;
        cut[0] = IDiamondCut.FacetCut({
            facetAddress: _diamondCutFacet,
            action: IDiamondCut.FacetCutAction.Add,
            functionSelectors: functionSelectors
        });
        PositionManagerStorage.diamondCut(cut, address(0), "");
        StorageStruct storage Storage = PositionManagerStorage.getStorage();
        Storage.registry = IRegistry(_registry);
    }

    function init(address _owner, address _uniswapAddressHolder) public onlyFactory initializer {
        StorageStruct storage Storage = PositionManagerStorage.getStorage();
        Storage.owner = _owner;
        Storage.uniswapAddressHolder = IUniswapAddressHolder(_uniswapAddressHolder);
    }

    function _genPositionId() internal returns (uint256) {
        return ++positionIdCounter;
    }

    ///@notice create position
    ///@param inputs struct containing position info
    ///@return positionId ID of the position
    function createPosition(
        IPositionManager.CreatePositionInput calldata inputs
    ) external override onlyWhitelisted returns (uint256 positionId) {
        positionId = _genPositionId();
        runningPositionIds.push(positionId);
        positions[positionId] = PositionInfo({
            tokenId: inputs.tokenId,
            strategyProvider: inputs.strategyProvider,
            strategyId: inputs.strategyId,
            totalDepositUSDValue: inputs.totalDepositUSDValue,
            amount0CollectedFee: 0,
            amount1CollectedFee: 0,
            amount0Leftover: inputs.amount0Leftover,
            amount1Leftover: inputs.amount1Leftover,
            tickLowerDiff: inputs.tickLowerDiff,
            tickUpperDiff: inputs.tickUpperDiff,
            amount0Returned: 0,
            amount1Returned: 0,
            amount0ReturnedUsdValue: 0,
            amount1ReturnedUsdValue: 0
        });

        _pushTokenId(inputs.tokenId);
        _setDefaultDataOfPosition(positionId);
    }

    ///@notice close position
    ///@param positionId ID of the position
    function _closePosition(uint256 positionId) internal positionRunning(positionId) {
        _removeFromRunningPosition(positionId);
        closedPositionIds.push(positionId);
    }

    function _removeFromRunningPosition(uint256 positionId) internal {
        for (uint256 i; i < runningPositionIds.length; ++i) {
            if (runningPositionIds[i] == positionId) {
                runningPositionIds[i] = runningPositionIds[runningPositionIds.length - 1];
                runningPositionIds.pop();
                break;
            }
        }
    }

    ///@notice update position total deposit USD value
    ///@param positionId ID of the position
    ///@param _totalDepositUSDValue The total deposit value in USD
    ///@param _amount0Leftover The amount of token0 leftover after increase liquidity
    ///@param _amount1Leftover The amount of token1 leftover after increase liquidity
    function middlewareIncreaseLiquidity(
        uint256 positionId,
        uint256 _totalDepositUSDValue,
        uint256 _amount0Leftover,
        uint256 _amount1Leftover
    ) external override onlyWhitelisted positionExists(positionId) {
        positions[positionId].totalDepositUSDValue = _totalDepositUSDValue;
        positions[positionId].amount0Leftover = _amount0Leftover;
        positions[positionId].amount1Leftover = _amount1Leftover;
    }

    ///@notice get position info from tokenId
    ///@param tokenId ID of the position
    ///@return positionInfo PositionInfo struct
    function getPositionInfoFromTokenId(
        uint256 tokenId
    ) external view override returns (PositionInfo memory positionInfo) {
        for (uint256 i; i < runningPositionIds.length; ++i) {
            uint256 positionId = runningPositionIds[i];
            if (positions[positionId].tokenId == tokenId) {
                return positions[positionId];
            }
        }
        for (uint256 i; i < closedPositionIds.length; ++i) {
            uint256 positionId = closedPositionIds[i];
            if (positions[positionId].tokenId == tokenId) {
                return positions[positionId];
            }
        }
        require(false, "PMS");
    }

    ///@notice get position info from positionId
    ///@param positionId ID of the position
    ///@return positionInfo PositionInfo struct
    function getPositionInfo(uint256 positionId) external view override returns (PositionInfo memory positionInfo) {
        positionInfo = positions[positionId];
        require(positionInfo.tokenId != 0, "PMS");
    }

    ///@notice check if the position is running
    ///@param positionId ID of the position
    ///@return bool true if the position is running
    function isPositionRunning(uint256 positionId) external view override returns (bool) {
        return _isPositionRunning(positionId);
    }

    ///@notice middleware function to update position info for rebalance
    ///@param positionId ID of the position
    ///@param newTokenId ID of the new NFT
    ///@param tickLowerDiff The difference between the current tick and the tickLower
    ///@param tickUpperDiff The difference between the current tick and the tickUpper
    ///@param amount0CollectedFee The amount of token0 collected fee after rebalance
    ///@param amount1CollectedFee The amount of token1 collected fee after rebalance
    function middlewareRebalance(
        uint256 positionId,
        uint256 newTokenId,
        int24 tickLowerDiff,
        int24 tickUpperDiff,
        uint256 amount0CollectedFee,
        uint256 amount1CollectedFee,
        uint256 amount0Leftover,
        uint256 amount1Leftover
    ) external override onlyWhitelisted positionRunning(positionId) {
        PositionInfo storage pInfo = positions[positionId];
        pInfo.tokenId = newTokenId;
        pInfo.tickLowerDiff = tickLowerDiff;
        pInfo.tickUpperDiff = tickUpperDiff;
        pInfo.amount0CollectedFee = amount0CollectedFee;
        pInfo.amount1CollectedFee = amount1CollectedFee;
        pInfo.amount0Leftover = amount0Leftover;
        pInfo.amount1Leftover = amount1Leftover;

        _pushTokenId(newTokenId);
    }

    ///@notice middleware function to update position info for withdraw
    ///@param input MiddlewareWithdrawInput struct
    function middlewareWithdraw(MiddlewareWithdrawInput memory input) external override onlyWhitelisted {
        PositionInfo storage pInfo = positions[input.positionId];
        pInfo.amount0CollectedFee = input.amount0CollectedFee;
        pInfo.amount1CollectedFee = input.amount1CollectedFee;
        pInfo.amount0Returned = input.amount0Returned;
        pInfo.amount1Returned = input.amount1Returned;
        pInfo.amount0ReturnedUsdValue = input.amount0ReturnedUsdValue;
        pInfo.amount1ReturnedUsdValue = input.amount1ReturnedUsdValue;
        pInfo.amount0Leftover = 0;
        pInfo.amount1Leftover = 0;
        _closePosition(input.positionId);
    }

    ///@notice add tokenId in the uniswapNFTs array
    ///@param tokenId ID of the added NFT
    function _pushTokenId(uint256 tokenId) internal onlyOwnedPosition(tokenId) {
        tokenIds.push(tokenId);
    }

    ///@notice get the IDs of uniswap NFTs
    ///@param cursor is the aforementioned cursor. It simply indicates the starting index for enumeration. The first call should pass 0, and subsequent calls should pass the returned newCursor.
    ///@param howMany indicates how many items should be returned. If there aren’t enough remaining items in the array, the function will return fewer items.
    ///@return nfts uint256[] return array of uniswap nft tokenId
    ///@return newCursor uint256 return the new cursor
    function getUniswapNFTs(
        uint256 cursor,
        uint256 howMany
    ) public view returns (uint256[] memory nfts, uint256 newCursor) {
        uint256 length = howMany;
        if (length > tokenIds.length - cursor) {
            length = tokenIds.length - cursor;
        }

        nfts = new uint256[](length);
        for (uint256 i = 0; i < length; i++) {
            nfts[i] = tokenIds[cursor + i];
        }

        return (nfts, cursor + length);
    }

    ///@notice return the length of the uniswap NFTs array
    ///@return length of the array
    function getUniswapNFTsLength() external view returns (uint256) {
        return tokenIds.length;
    }

    ///@notice get the IDs of the running positions
    ///@param cursor is the aforementioned cursor. It simply indicates the starting index for enumeration. The first call should pass 0, and subsequent calls should pass the returned newCursor.
    ///@param howMany indicates how many items should be returned. If there aren’t enough remaining items in the array, the function will return fewer items.
    ///@return runningPositions uint256[] return array of PositionManager running position IDs
    ///@return newCursor uint256 return the new cursor
    function getRunningPositions(
        uint256 cursor,
        uint256 howMany
    ) public view returns (uint256[] memory runningPositions, uint256 newCursor) {
        uint256 length = howMany;
        if (length > runningPositionIds.length - cursor) {
            length = runningPositionIds.length - cursor;
        }

        runningPositions = new uint256[](length);
        for (uint256 i = 0; i < length; i++) {
            runningPositions[i] = runningPositionIds[cursor + i];
        }

        return (runningPositions, cursor + length);
    }

    ///@notice return the length of the running positions array
    ///@return length of the array
    function getRunningPositionsLength() external view returns (uint256) {
        return runningPositionIds.length;
    }

    ///@notice get the IDs of the closed positions
    ///@param cursor is the aforementioned cursor. It simply indicates the starting index for enumeration. The first call should pass 0, and subsequent calls should pass the returned newCursor.
    ///@param howMany indicates how many items should be returned. If there aren’t enough remaining items in the array, the function will return fewer items.
    ///@return closedPositions uint256[] return array of PositionManager closed position IDs
    ///@return newCursor uint256 return the new cursor
    function getClosedPositions(
        uint256 cursor,
        uint256 howMany
    ) public view returns (uint256[] memory closedPositions, uint256 newCursor) {
        uint256 length = howMany;
        if (length > closedPositionIds.length - cursor) {
            length = closedPositionIds.length - cursor;
        }

        closedPositions = new uint256[](length);
        for (uint256 i = 0; i < length; i++) {
            closedPositions[i] = closedPositionIds[cursor + i];
        }

        return (closedPositions, cursor + length);
    }

    ///@notice return the length of the closed positions array
    ///@return length of the array
    function getClosedPositionsLength() external view returns (uint256) {
        return closedPositionIds.length;
    }

    ///@notice set default data for every module
    ///@param positionId ID of the position
    function _setDefaultDataOfPosition(uint256 positionId) internal {
        StorageStruct storage Storage = PositionManagerStorage.getStorage();

        bytes32[] memory moduleKeys = Storage.registry.getModuleKeys();

        uint256 moduleKeysLength = moduleKeys.length;
        for (uint256 i; i < moduleKeysLength; ++i) {
            IRegistry.Entry memory entry = Storage.registry.getModuleInfo(moduleKeys[i]);
            positionToModuleData[positionId][entry.contractAddress] = entry.defaultData;
        }
    }

    ///@notice sets the data of a module of position
    ///@param positionId ID of the position
    ///@param moduleAddress address of the module
    ///@param data data for the module
    function setModuleData(uint256 positionId, address moduleAddress, bytes32 data) external override onlyWhitelisted {
        positionToModuleData[positionId][moduleAddress] = data;
    }

    ///@notice get data for a module of position
    ///@param _positionId ID of the position
    ///@param _moduleAddress address of the module
    ///@return data of the module
    function getPositionModuleData(
        uint256 _positionId,
        address _moduleAddress
    ) public view override returns (bytes32 data) {
        return (positionToModuleData[_positionId][_moduleAddress]);
    }

    ///@notice get position lower tick diff and upper tick diff
    ///@param positionId ID of the position
    ///@return tickLowerDiff difference between the current tick of the position and the provied lower tick
    ///@return tickUpperDiff difference between the current tick of the position and the provied upper tick
    function getPositionTickDiffs(
        uint256 positionId
    ) external view override positionExists(positionId) returns (int24, int24) {
        return (positions[positionId].tickLowerDiff, positions[positionId].tickUpperDiff);
    }

    ///@notice return the address of this position manager owner
    ///@return address of the owner
    function getOwner() external view override returns (address) {
        StorageStruct storage Storage = PositionManagerStorage.getStorage();
        return Storage.owner;
    }

    ///@notice transfer ERC20 tokens stuck in Position Manager to owner
    ///@param tokenAddress address of the token to be withdrawn
    function withdrawERC20ToOwner(address tokenAddress) external override onlyGovernance {
        StorageStruct storage Storage = PositionManagerStorage.getStorage();
        address owner = Storage.owner;
        uint256 amount = ERC20Helper._getBalance(tokenAddress, address(this));
        uint256 got = ERC20Helper._withdrawTokens(tokenAddress, owner, amount);

        require(amount == got, "PME");
        emit ERC20Withdrawn(tokenAddress, owner, got);
    }

    function _isPositionRunning(uint256 positionId) internal view returns (bool) {
        for (uint256 i = 0; i < runningPositionIds.length; ++i) {
            if (runningPositionIds[i] == positionId) {
                return true;
            }
        }
        return false;
    }

    ///@notice function to check if an address corresponds to an active module (or this contract)
    ///@param _address input address
    ///@return boolean true if the address is an active module
    function _calledFromActiveModule(address _address) internal view returns (bool) {
        StorageStruct storage Storage = PositionManagerStorage.getStorage();
        bytes32[] memory keys = Storage.registry.getModuleKeys();

        uint256 keysLength = keys.length;
        for (uint256 i; i < keysLength; ++i) {
            if (Storage.registry.getModuleInfo(keys[i]).contractAddress == _address) {
                return true;
            }
        }
        return false;
    }

    fallback() external payable onlyWhitelisted {
        StorageStruct storage Storage = PositionManagerStorage.getStorage();

        address facet = Storage.selectorToFacetAndPosition[msg.sig].facetAddress;
        require(facet != address(0), "PM");

        ///@dev Execute external function from facet using delegatecall and return any value.
        assembly {
            // copy function selector and any arguments
            calldatacopy(0, 0, calldatasize())
            // execute function call using the facet
            let result := delegatecall(gas(), facet, 0, calldatasize(), 0, 0)
            // get any return value
            returndatacopy(0, 0, returndatasize())
            // return any return value or error back to the caller
            switch result
            case 0 {
                revert(0, returndatasize())
            }
            default {
                return(0, returndatasize())
            }
        }
    }

    receive() external payable {
        revert();
        //we need to decide what to do when the contract receives ether
        //for now we just revert
    }
}

contracts/PositionManagerFactory.sol.content:
// SPDX-License-Identifier: GPL-2.0

pragma solidity 0.7.6;
pragma abicoder v2;

import "./PositionManager.sol";
import "./interfaces/IPositionManagerFactory.sol";
import "@openzeppelin/contracts/math/SafeMath.sol";
import "./interfaces/IStrategyProviderWalletFactory.sol";

contract PositionManagerFactory is IPositionManagerFactory {
    using SafeMath for uint256;

    address public immutable registry;
    address public immutable diamondCutFacet;
    address public immutable uniswapAddressHolder;
    address[] public positionManagers;
    IDiamondCut.FacetCut[] public actions;
    mapping(address => address) public override userToPositionManager;

    ///@notice emitted when a new position manager is created
    ///@param positionManager address of PositionManager
    ///@param user address of user
    event PositionManagerCreated(address indexed positionManager, address user);

    modifier onlyGovernance() {
        require(msg.sender == IRegistry(registry).governance(), "PFG");
        _;
    }

    constructor(address _registry, address _diamondCutFacet, address _uniswapAddressHolder) {
        registry = _registry;
        diamondCutFacet = _diamondCutFacet;
        uniswapAddressHolder = _uniswapAddressHolder;
    }

    ///@notice update actions already existing on positionManager
    ///@dev Add (0) Replace(1) Remove(2)
    ///@param positionManager address of the position manager on which one should modified an action
    ///@param actionsToUpdate contains the facet addresses and function selectors of the actions
    function updateDiamond(
        address positionManager,
        IDiamondCut.FacetCut[] memory actionsToUpdate
    ) external onlyGovernance {
        IDiamondCut(positionManager).diamondCut(actionsToUpdate, address(0), "");
    }

    ///@notice adds or removes an action to/from the factory
    ///@param facetAction facet of the action to add or remove from position manager factory
    function updateActionData(IDiamondCut.FacetCut calldata facetAction) external onlyGovernance {
        if (facetAction.action == IDiamondCut.FacetCutAction.Remove) {
            uint256 actionsLength = actions.length;
            for (uint256 i; i < actionsLength; ++i) {
                if (actions[i].facetAddress == facetAction.facetAddress) {
                    actions[i] = actions[actionsLength - 1];
                    actions.pop();
                    return;
                }
            }
            require(false, "PFU");
        }

        if (facetAction.action == IDiamondCut.FacetCutAction.Replace) {
            uint256 actionsLength = actions.length;
            for (uint256 i; i < actionsLength; ++i) {
                if (actions[i].facetAddress == facetAction.facetAddress) {
                    actions[i] = facetAction;
                    return;
                }
            }
            require(false, "PFU");
        }

        if (facetAction.action == IDiamondCut.FacetCutAction.Add) {
            uint256 actionsLength = actions.length;
            for (uint256 i; i < actionsLength; ++i) {
                require(actions[i].facetAddress != facetAction.facetAddress, "PFU");
            }
            actions.push(facetAction);
            return;
        }

        require(false, "PFU");
    }

    ///@notice deploy new positionManager and assign to userAddress
    ///@return address return new PositionManager address
    function create() external override returns (address) {
        require(userToPositionManager[msg.sender] == address(0), "PFP");

        PositionManager manager = new PositionManager(msg.sender, diamondCutFacet, registry);
        positionManagers.push(address(manager));
        userToPositionManager[msg.sender] = address(manager);
        manager.init(msg.sender, uniswapAddressHolder);
        IDiamondCut(address(manager)).diamondCut(actions, address(0), "");

        ///@dev create strategy provider wallet
        _createStrategyProviderWallet(msg.sender);

        emit PositionManagerCreated(address(manager), msg.sender);

        return address(manager);
    }

    ///@notice create strategy provider wallet
    function _createStrategyProviderWallet(address provider) internal {
        IStrategyProviderWalletFactory(IRegistry(registry).strategyProviderWalletFactoryAddress()).create(provider);
    }

    ///@notice get the array of position manager addresses
    ///@param cursor is the aforementioned cursor. It simply indicates the starting index for enumeration. The first call should pass 0, and subsequent calls should pass the returned newCursor.
    ///@param howMany indicates how many items should be returned. If there aren’t enough remaining items in the array, the function will return fewer items.
    ///@return managers address[] return array of PositionManager addresses
    ///@return newCursor uint256 return the new cursor
    function getPositionManagers(
        uint256 cursor,
        uint256 howMany
    ) public view returns (address[] memory managers, uint256 newCursor) {
        uint256 length = howMany;
        if (length > positionManagers.length - cursor) {
            length = positionManagers.length - cursor;
        }

        managers = new address[](length);
        for (uint256 i = 0; i < length; i++) {
            managers[i] = positionManagers[cursor + i];
        }

        return (managers, cursor + length);
    }

    ///@notice get the length of position manager array
    ///@return uint256 return length of PositionManager array
    function getPositionManagersLength() external view returns (uint256) {
        return positionManagers.length;
    }
}

contracts/recipes/DepositRecipes.sol.content:
// SPDX-License-Identifier: GPL-2.0

pragma solidity 0.7.6;
pragma abicoder v2;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/SafeERC20.sol";
import "@openzeppelin/contracts/math/SafeMath.sol";
import "@uniswap/v3-periphery/contracts/interfaces/INonfungiblePositionManager.sol";
import "@uniswap/v3-core/contracts/interfaces/IUniswapV3Factory.sol";
import "../interfaces/recipes/IDepositRecipes.sol";
import "../interfaces/actions/IMint.sol";
import "../interfaces/actions/IZapIn.sol";
import "../interfaces/actions/IIncreaseLiquidity.sol";
import "../interfaces/actions/ISingleTokenIncreaseLiquidity.sol";
import "../interfaces/IPositionManager.sol";
import "../interfaces/IPositionManagerFactory.sol";
import "../interfaces/IUniswapAddressHolder.sol";
import "../interfaces/IRegistry.sol";
import "../interfaces/IERC20Extended.sol";
import "../interfaces/IStrategyProviderWalletFactory.sol";
import "../interfaces/IStrategyProviderWallet.sol";
import "../libraries/UniswapHelper.sol";
import "../libraries/UniswapQuoter.sol";
import "../libraries/SafeInt24Math.sol";

///@notice DepositRecipes allows user to fill their position manager with UniswapV3 positions
///        by depositing an already minted NFT or by minting directly a new one
contract DepositRecipes is IDepositRecipes {
    using SafeERC20 for IERC20;
    using SafeMath for uint256;
    using SafeInt24Math for int24;

    IUniswapAddressHolder public immutable uniswapAddressHolder;
    IRegistry public immutable registry;

    ///@notice emitted when a position is created
    ///@param positionManager address of the position manager
    ///@param from address of the user
    ///@param positionId ID of the position
    ///@param strategyId ID of the strategy
    event PositionDeposited(address indexed positionManager, address from, uint256 positionId, bytes16 strategyId);

    ///@notice emitted when a position is created
    ///@param positionManager address of the position manager
    ///@param from address of the user
    ///@param positionId ID of the position
    ///@param strategyId ID of the strategy
    ///@param strategyProvider address of the strategy provider
    event PositionDepositedListedStrategy(
        address indexed positionManager,
        address from,
        uint256 positionId,
        bytes16 strategyId,
        address strategyProvider
    );

    ///@notice emitted when a position is increased liquidity
    ///@param positionManager address of the position manager
    ///@param from address of the user
    ///@param positionId ID of the position
    event PositionIncreasedLiquidity(address indexed positionManager, address from, uint256 positionId);

    constructor(address _registry, address _uniswapAddressHolder) {
        require(_uniswapAddressHolder != address(0), "DRCA0");
        require(_registry != address(0), "DRCA0");

        uniswapAddressHolder = IUniswapAddressHolder(_uniswapAddressHolder);
        registry = IRegistry(_registry);
    }

    ///@notice mint uniswapV3 NFT and deposit in the position manager
    ///@param input struct of DepositInput parameters
    ///@return tokenId the ID of the minted NFT
    function deposit(DepositInput memory input) external returns (uint256 tokenId) {
        checkTokensValid(input.token0, input.token1);

        require(input.amount0Desired != 0 || input.amount1Desired != 0, "DRA0");
        require(registry.isAllowableFeeTier(input.fee), "DRFT");
        require(input.tickLowerDiff <= 0 && input.tickUpperDiff >= 0, "DRTD");
        require(input.token0 != input.token1, "DRT");

        address positionManager = IPositionManagerFactory(registry.positionManagerFactoryAddress())
            .userToPositionManager(msg.sender);
        require(positionManager != address(0), "DRPM0");

        bool isOrderChanged;
        (input.token0, input.token1, isOrderChanged) = UniswapHelper._reorderTokens(input.token0, input.token1);

        (input.amount0Desired, input.amount1Desired) = isOrderChanged
            ? (input.amount1Desired, input.amount0Desired)
            : (input.amount0Desired, input.amount1Desired);

        int24 currentTick = UniswapHelper._getCurrentTick(
            uniswapAddressHolder.uniswapV3FactoryAddress(),
            input.token0,
            input.token1,
            input.fee
        );

        ///@dev calculate the correct amounts before transferring tokens to position manager
        (, input.amount0Desired, input.amount1Desired) = UniswapHelper.calLiquidityAndAmounts(
            uniswapAddressHolder.uniswapV3FactoryAddress(),
            input.token0,
            input.token1,
            input.fee,
            currentTick.add(input.tickLowerDiff),
            currentTick.add(input.tickUpperDiff),
            input.amount0Desired,
            input.amount1Desired
        );

        ///@dev send tokens to position manager to be able to call the mint action
        IERC20(input.token0).safeTransferFrom(msg.sender, positionManager, input.amount0Desired);
        IERC20(input.token1).safeTransferFrom(msg.sender, positionManager, input.amount1Desired);

        uint256 amount0Deposit;
        uint256 amount1Deposit;
        (tokenId, amount0Deposit, amount1Deposit) = IMint(positionManager).mint(
            IMint.MintInput({
                token0Address: input.token0,
                token1Address: input.token1,
                fee: input.fee,
                tickLower: currentTick.add(input.tickLowerDiff),
                tickUpper: currentTick.add(input.tickUpperDiff),
                amount0Desired: input.amount0Desired,
                amount1Desired: input.amount1Desired,
                isReturnLeftOver: true
            })
        );

        // calculate total usd value of the position
        uint256 totalUsdValue = _calTotalDepositUsdValue(input.token0, input.token1, amount0Deposit, amount1Deposit);

        uint256 positionId = IPositionManager(positionManager).createPosition(
            IPositionManager.CreatePositionInput({
                tokenId: tokenId,
                strategyProvider: address(0),
                strategyId: input.strategyId,
                totalDepositUSDValue: totalUsdValue,
                tickLowerDiff: input.tickLowerDiff,
                tickUpperDiff: input.tickUpperDiff,
                amount0Leftover: 0,
                amount1Leftover: 0
            })
        );

        emit PositionDeposited(positionManager, msg.sender, positionId, input.strategyId);
    }

    ///@notice mint uniswapV3 NFT and deposit in the position manager by following listed strategy
    ///@param input struct of DepositListedStrategyInput parameters
    ///@return tokenId the ID of the minted NFT
    function depositListedStrategy(DepositListedStrategyInput memory input) external returns (uint256 tokenId) {
        checkStrategyExist(input.strategyProvider, input.strategyId, input.token0, input.token1, input.fee);
        checkTokensValid(input.token0, input.token1);

        require(input.amount0Desired != 0 || input.amount1Desired != 0, "DRA0");
        require(registry.isAllowableFeeTier(input.fee), "DRFT");
        require(input.tickLowerDiff <= 0 && input.tickUpperDiff >= 0, "DRTD");
        require(input.token0 != input.token1, "DRT");

        address positionManager = IPositionManagerFactory(registry.positionManagerFactoryAddress())
            .userToPositionManager(msg.sender);
        require(positionManager != address(0), "DRPM0");

        bool isOrderChanged;
        (input.token0, input.token1, isOrderChanged) = UniswapHelper._reorderTokens(input.token0, input.token1);

        (input.amount0Desired, input.amount1Desired) = isOrderChanged
            ? (input.amount1Desired, input.amount0Desired)
            : (input.amount0Desired, input.amount1Desired);

        int24 currentTick = UniswapHelper._getCurrentTick(
            uniswapAddressHolder.uniswapV3FactoryAddress(),
            input.token0,
            input.token1,
            input.fee
        );

        ///@dev calculate the correct amounts before transferring tokens to position manager
        (, input.amount0Desired, input.amount1Desired) = UniswapHelper.calLiquidityAndAmounts(
            uniswapAddressHolder.uniswapV3FactoryAddress(),
            input.token0,
            input.token1,
            input.fee,
            currentTick.add(input.tickLowerDiff),
            currentTick.add(input.tickUpperDiff),
            input.amount0Desired,
            input.amount1Desired
        );

        ///@dev send tokens to position manager to be able to call the mint action
        IERC20(input.token0).safeTransferFrom(msg.sender, positionManager, input.amount0Desired);
        IERC20(input.token1).safeTransferFrom(msg.sender, positionManager, input.amount1Desired);

        uint256 totalUsdValue;
        {
            uint256 amount0Deposit;
            uint256 amount1Deposit;
            (tokenId, amount0Deposit, amount1Deposit) = IMint(positionManager).mint(
                IMint.MintInput({
                    token0Address: input.token0,
                    token1Address: input.token1,
                    fee: input.fee,
                    tickLower: currentTick.add(input.tickLowerDiff),
                    tickUpper: currentTick.add(input.tickUpperDiff),
                    amount0Desired: input.amount0Desired,
                    amount1Desired: input.amount1Desired,
                    isReturnLeftOver: true
                })
            );

            // calculate total usd value of the position
            totalUsdValue = _calTotalDepositUsdValue(input.token0, input.token1, amount0Deposit, amount1Deposit);
        }

        uint256 positionId = IPositionManager(positionManager).createPosition(
            IPositionManager.CreatePositionInput({
                tokenId: tokenId,
                strategyProvider: input.strategyProvider,
                strategyId: input.strategyId,
                totalDepositUSDValue: totalUsdValue,
                tickLowerDiff: input.tickLowerDiff,
                tickUpperDiff: input.tickUpperDiff,
                amount0Leftover: 0,
                amount1Leftover: 0
            })
        );

        emit PositionDepositedListedStrategy(
            positionManager,
            msg.sender,
            positionId,
            input.strategyId,
            input.strategyProvider
        );
    }

    ///@notice increases liquidity of a position
    ///@param positionId id of the position
    ///@param amount0Desired amount of token0 to be added to the position
    ///@param amount1Desired amount of token1 to be added to the position
    function increaseLiquidity(uint256 positionId, uint256 amount0Desired, uint256 amount1Desired) external {
        require(amount0Desired != 0 || amount1Desired != 0, "DRA0");
        address positionManager = IPositionManagerFactory(registry.positionManagerFactoryAddress())
            .userToPositionManager(msg.sender);
        require(positionManager != address(0), "DRPM0");

        IPositionManager.PositionInfo memory pInfo = IPositionManager(positionManager).getPositionInfo(positionId);

        (address token0, address token1, uint24 fee, int24 tickLower, int24 tickUpper) = UniswapHelper._getTokens(
            pInfo.tokenId,
            INonfungiblePositionManager(uniswapAddressHolder.nonfungiblePositionManagerAddress())
        );

        ///@dev calculate the correct amounts before transferring tokens to position manager
        (, amount0Desired, amount1Desired) = UniswapHelper.calLiquidityAndAmounts(
            uniswapAddressHolder.uniswapV3FactoryAddress(),
            token0,
            token1,
            fee,
            tickLower,
            tickUpper,
            amount0Desired,
            amount1Desired
        );

        ///@dev send tokens to position manager to be able to call the increase liquidity action
        IERC20(token0).safeTransferFrom(msg.sender, positionManager, amount0Desired);
        IERC20(token1).safeTransferFrom(msg.sender, positionManager, amount1Desired);

        (uint256 amount0Increased, uint256 amount1Increased) = IIncreaseLiquidity(positionManager).increaseLiquidity(
            IIncreaseLiquidity.IncreaseLiquidityInput({
                tokenId: pInfo.tokenId,
                token0Address: token0,
                token1Address: token1,
                amount0Desired: amount0Desired,
                amount1Desired: amount1Desired
            })
        );

        // calculate total usd value of the position
        uint256 totalUsdValue = _calTotalDepositUsdValue(token0, token1, amount0Increased, amount1Increased);

        IPositionManager(positionManager).middlewareIncreaseLiquidity(
            positionId,
            pInfo.totalDepositUSDValue.add(totalUsdValue),
            pInfo.amount0Leftover,
            pInfo.amount1Leftover
        );

        emit PositionIncreasedLiquidity(positionManager, msg.sender, positionId);
    }

    ///@notice mints a uni NFT with a single input token
    ///@param input struct of SingleTokenDepositInput parameters
    ///@return tokenId the ID of the minted NFT
    function singleTokenDeposit(SingleTokenDepositInput memory input) external returns (uint256 tokenId) {
        checkTokensValid(input.token0, input.token1);

        require(input.amountIn != 0, "DRA0");
        require(registry.isAllowableFeeTier(input.fee), "DRFT");
        require(input.tickLowerDiff <= 0 && input.tickUpperDiff >= 0, "DRTD");
        require(input.token0 != input.token1, "DRT");
        address positionManager = IPositionManagerFactory(registry.positionManagerFactoryAddress())
            .userToPositionManager(msg.sender);
        require(positionManager != address(0), "DRPM0");

        bool isOrderChanged;
        (input.token0, input.token1, isOrderChanged) = UniswapHelper._reorderTokens(input.token0, input.token1);
        input.isToken0In = isOrderChanged ? !input.isToken0In : input.isToken0In;

        int24 currentTick = UniswapHelper._getCurrentTick(
            uniswapAddressHolder.uniswapV3FactoryAddress(),
            input.token0,
            input.token1,
            input.fee
        );

        ///@dev send tokens to position manager to be able to call the zap in action
        IERC20(input.isToken0In ? input.token0 : input.token1).safeTransferFrom(
            msg.sender,
            positionManager,
            input.amountIn
        );

        IZapIn.ZapInOutput memory zapInOutput = IZapIn(positionManager).zapIn(
            IZapIn.ZapInInput(
                input.token0,
                input.token1,
                input.isToken0In,
                input.amountIn,
                currentTick.add(input.tickLowerDiff),
                currentTick.add(input.tickUpperDiff),
                input.fee
            )
        );

        // calculate total usd value of the position
        uint256 totalUsdValue = _calTotalDepositUsdValue(
            input.token0,
            input.token1,
            zapInOutput.amount0Deposited,
            zapInOutput.amount1Deposited
        );

        uint256 positionId = IPositionManager(positionManager).createPosition(
            IPositionManager.CreatePositionInput({
                tokenId: tokenId,
                strategyProvider: address(0),
                strategyId: input.strategyId,
                totalDepositUSDValue: totalUsdValue,
                tickLowerDiff: input.tickLowerDiff,
                tickUpperDiff: input.tickUpperDiff,
                amount0Leftover: zapInOutput.amount0Leftover,
                amount1Leftover: zapInOutput.amount1Leftover
            })
        );

        emit PositionDeposited(positionManager, msg.sender, positionId, input.strategyId);
    }

    ///@notice mints a uni NFT with a single input token follow listed strategy
    ///@param input struct of SingleTokenDepositListedStrategyInput parameters
    ///@return tokenId the ID of the minted NFT
    function singleTokenDepositListedStrategy(
        SingleTokenDepositListedStrategyInput memory input
    ) external returns (uint256 tokenId) {
        checkStrategyExist(input.strategyProvider, input.strategyId, input.token0, input.token1, input.fee);
        checkTokensValid(input.token0, input.token1);

        require(input.amountIn != 0, "DRA0");
        require(registry.isAllowableFeeTier(input.fee), "DRFT");
        require(input.tickLowerDiff <= 0 && input.tickUpperDiff >= 0, "DRTD");
        require(input.token0 != input.token1, "DRT");

        address positionManager = IPositionManagerFactory(registry.positionManagerFactoryAddress())
            .userToPositionManager(msg.sender);
        require(positionManager != address(0), "DRPM0");

        bool isOrderChanged;
        (input.token0, input.token1, isOrderChanged) = UniswapHelper._reorderTokens(input.token0, input.token1);
        input.isToken0In = isOrderChanged ? !input.isToken0In : input.isToken0In;

        int24 currentTick = UniswapHelper._getCurrentTick(
            uniswapAddressHolder.uniswapV3FactoryAddress(),
            input.token0,
            input.token1,
            input.fee
        );

        ///@dev send tokens to position manager to be able to call the zap in action
        IERC20(input.isToken0In ? input.token0 : input.token1).safeTransferFrom(
            msg.sender,
            positionManager,
            input.amountIn
        );

        uint256 totalUsdValue;
        IZapIn.ZapInOutput memory zapInOutput;
        {
            zapInOutput = IZapIn(positionManager).zapIn(
                IZapIn.ZapInInput(
                    input.token0,
                    input.token1,
                    input.isToken0In,
                    input.amountIn,
                    currentTick.add(input.tickLowerDiff),
                    currentTick.add(input.tickUpperDiff),
                    input.fee
                )
            );

            // calculate total usd value of the position
            totalUsdValue = _calTotalDepositUsdValue(
                input.token0,
                input.token1,
                zapInOutput.amount0Deposited,
                zapInOutput.amount1Deposited
            );
        }

        uint256 positionId = IPositionManager(positionManager).createPosition(
            IPositionManager.CreatePositionInput({
                tokenId: tokenId,
                strategyProvider: input.strategyProvider,
                strategyId: input.strategyId,
                totalDepositUSDValue: totalUsdValue,
                tickLowerDiff: input.tickLowerDiff,
                tickUpperDiff: input.tickUpperDiff,
                amount0Leftover: zapInOutput.amount0Leftover,
                amount1Leftover: zapInOutput.amount1Leftover
            })
        );

        emit PositionDepositedListedStrategy(
            positionManager,
            msg.sender,
            positionId,
            input.strategyId,
            input.strategyProvider
        );
    }

    ///@notice increases liquidity of a position with sigle token
    ///@param positionId id of the position
    ///@param isToken0In true if the input token is token0, false if the input token is token1
    ///@param amount amount of input token
    function sigleTokenIncreaseLiquidity(uint256 positionId, bool isToken0In, uint256 amount) external {
        require(amount != 0, "DRA0");
        address positionManager = IPositionManagerFactory(registry.positionManagerFactoryAddress())
            .userToPositionManager(msg.sender);
        require(positionManager != address(0), "DRPM0");

        IPositionManager.PositionInfo memory pInfo = IPositionManager(positionManager).getPositionInfo(positionId);

        (address token0, address token1, uint24 fee, int24 tickLower, int24 tickUpper) = UniswapHelper._getTokens(
            pInfo.tokenId,
            INonfungiblePositionManager(uniswapAddressHolder.nonfungiblePositionManagerAddress())
        );

        ///@dev send tokens to position manager to be able to call the sigleTokenIncreaseLiquidity action
        IERC20(isToken0In ? token0 : token1).safeTransferFrom(msg.sender, positionManager, amount);

        ISingleTokenIncreaseLiquidity.SingleTokenIncreaseLiquidityOutput
            memory singleTokenIncreaseLiquidityOutput = ISingleTokenIncreaseLiquidity(positionManager)
                .singleTokenIncreaseLiquidity(
                    ISingleTokenIncreaseLiquidity.SingleTokenIncreaseLiquidityInput({
                        tokenId: pInfo.tokenId,
                        token0: token0,
                        token1: token1,
                        isToken0In: isToken0In,
                        amountIn: amount,
                        tickLower: tickLower,
                        tickUpper: tickUpper,
                        fee: fee
                    })
                );

        // calculate total usd value of the position
        uint256 totalUsdValue = _calTotalDepositUsdValue(
            token0,
            token1,
            singleTokenIncreaseLiquidityOutput.amount0Increased,
            singleTokenIncreaseLiquidityOutput.amount1Increased
        );

        IPositionManager(positionManager).middlewareIncreaseLiquidity(
            positionId,
            pInfo.totalDepositUSDValue.add(totalUsdValue),
            pInfo.amount0Leftover.add(singleTokenIncreaseLiquidityOutput.amount0Leftover),
            pInfo.amount1Leftover.add(singleTokenIncreaseLiquidityOutput.amount1Leftover)
        );

        emit PositionIncreasedLiquidity(positionManager, msg.sender, positionId);
    }

    function checkStrategyExist(
        address provider,
        bytes16 strategyId,
        address token0,
        address token1,
        uint24 fee
    ) internal view {
        if (provider != address(0)) {
            address wallet = IStrategyProviderWalletFactory(registry.strategyProviderWalletFactoryAddress())
                .providerToWallet(provider);

            require(wallet != address(0), "DRSPW");
            address _pool = IUniswapV3Factory(uniswapAddressHolder.uniswapV3FactoryAddress()).getPool(
                token0,
                token1,
                fee
            );
            require(_pool != address(0), "DRP0");
            require(IStrategyProviderWallet(wallet).getStrategyInfo(strategyId).pool != _pool, "DRSPWSE");
        }
    }

    function checkTokensValid(address token0, address token1) internal view {
        _checkTokenCanBeSwapToWETH9(token0);
        _checkTokenCanBeSwapToWETH9(token1);
    }

    function _calTotalDepositUsdValue(
        address token0,
        address token1,
        uint256 amount0,
        uint256 amount1
    ) internal view returns (uint256 totalUsdValue) {
        uint24[] memory allowableFeeTiers = registry.getAllowableFeeTiers();
        totalUsdValue = _toUsdValue(token0, amount0, allowableFeeTiers).add(
            _toUsdValue(token1, amount1, allowableFeeTiers)
        );
    }

    function _toUsdValue(
        address tokenAddress,
        uint256 amount,
        uint24[] memory allowableFeeTiers
    ) internal view returns (uint256) {
        address usdTokenAddress = registry.usdValueTokenAddress();

        if (tokenAddress == usdTokenAddress) return amount;

        address deepestPool = UniswapHelper._findV3DeepestPool(
            uniswapAddressHolder.uniswapV3FactoryAddress(),
            tokenAddress,
            usdTokenAddress,
            allowableFeeTiers
        );

        (uint160 sqrtPriceX96, , , , , , ) = IUniswapV3Pool(deepestPool).slot0();

        return UniswapQuoter.quoteWithSqrtPrice(tokenAddress, usdTokenAddress, amount, sqrtPriceX96);
    }

    function _checkTokenCanBeSwapToWETH9(address token) internal view {
        address weth = registry.weth9();
        address usdValueTokenAddress = registry.usdValueTokenAddress();

        if (token == weth || token == usdValueTokenAddress) {
            return;
        }

        require(
            UniswapHelper._isPoolExist(
                uniswapAddressHolder.uniswapV3FactoryAddress(),
                token,
                usdValueTokenAddress,
                registry.getAllowableFeeTiers()
            ),
            "DRCSTW"
        );
    }
}

contracts/recipes/WithdrawRecipes.sol.content:
// SPDX-License-Identifier: GPL-2.0

pragma solidity 0.7.6;
pragma abicoder v2;

import "@openzeppelin/contracts/math/SafeMath.sol";
import "../libraries/UniswapHelper.sol";
import "../libraries/UniswapQuoter.sol";
import "../interfaces/IPositionManager.sol";
import "../interfaces/IPositionManagerFactory.sol";
import "../interfaces/IUniswapAddressHolder.sol";
import "../interfaces/actions/IClosePosition.sol";
import "../interfaces/actions/IReturnProfit.sol";
import "../interfaces/actions/IShareProfit.sol";
import "../interfaces/IRegistry.sol";
import "../interfaces/IStrategyProviderWalletFactory.sol";
import "../interfaces/IStrategyProviderWallet.sol";

///@notice WithdrawRecipes allows user to withdraw positions from PositionManager
contract WithdrawRecipes {
    using SafeMath for uint256;

    IUniswapAddressHolder public immutable uniswapAddressHolder;
    IRegistry public immutable registry;

    ///@notice emitted when a position is created
    ///@param positionManager the address of the position manager which recieved the position
    ///@param from address of the user
    ///@param positionId ID of the position
    ///@param tokenId ID of the minted NFT
    event PositionWithdrawan(address indexed positionManager, address from, uint256 positionId, uint256 tokenId);

    modifier positionIsRunning(uint256 positionId) {
        require(
            IPositionManager(
                IPositionManagerFactory(registry.positionManagerFactoryAddress()).userToPositionManager(msg.sender)
            ).isPositionRunning(positionId),
            "WRPIR"
        );
        _;
    }

    constructor(address _registry, address _uniswapAddressHolder) {
        require(_uniswapAddressHolder != address(0), "WRCA0");
        require(_registry != address(0), "WRCA0");

        uniswapAddressHolder = IUniswapAddressHolder(_uniswapAddressHolder);
        registry = IRegistry(_registry);
    }

    ///@notice closed position to the position manager with single token
    ///@param positionId ID of closed position
    function withdraw(uint256 positionId) external positionIsRunning(positionId) {
        address positionManager = IPositionManagerFactory(registry.positionManagerFactoryAddress())
            .userToPositionManager(msg.sender);
        require(positionManager != address(0), "WRPM0");

        IPositionManager.PositionInfo memory pInfo = IPositionManager(positionManager).getPositionInfo(positionId);

        (address token0, address token1, , , ) = UniswapHelper._getTokens(
            pInfo.tokenId,
            INonfungiblePositionManager(uniswapAddressHolder.nonfungiblePositionManagerAddress())
        );

        ///@dev close position
        (
            uint256 amount0CollectedFee,
            uint256 amount1CollectedFee,
            uint256 amount0Removed,
            uint256 amount1Removed
        ) = IClosePosition(positionManager).closePosition(pInfo.tokenId, false);

        IPositionManager.MiddlewareWithdrawInput memory mwInput;
        mwInput.positionId = positionId;

        if (pInfo.strategyProvider == address(0)) {
            IReturnProfit.ReturnProfitInput memory rpInput = IReturnProfit.ReturnProfitInput({
                token0: token0,
                token1: token1,
                amount0: amount0Removed.add(amount0CollectedFee).add(pInfo.amount0Leftover),
                amount1: amount1Removed.add(amount1CollectedFee).add(pInfo.amount1Leftover),
                returnedToken: address(0)
            });
            ///@dev return profit to user
            IReturnProfit.ReturnProfitOutput memory rpOutput = IReturnProfit(positionManager).returnProfit(rpInput);
            mwInput.amount0Returned = rpOutput.amount0Returned;
            mwInput.amount1Returned = rpOutput.amount1Returned;
            (mwInput.amount0ReturnedUsdValue, mwInput.amount1ReturnedUsdValue) = _calTokensUsdValue(
                token0,
                token1,
                rpOutput.amount0Returned,
                rpOutput.amount1Returned
            );
        } else {
            address strategyProviderWallet = IStrategyProviderWalletFactory(
                registry.strategyProviderWalletFactoryAddress()
            ).providerToWallet(pInfo.strategyProvider);

            IStrategyProviderWallet.StrategyInfo memory sInfo = IStrategyProviderWallet(strategyProviderWallet)
                .getStrategyInfo(pInfo.strategyId);

            IShareProfit.ShareProfitInput memory spInput = IShareProfit.ShareProfitInput({
                token0: token0,
                token1: token1,
                returnedToken: address(0),
                amount0: amount0Removed.add(amount0CollectedFee).add(pInfo.amount0Leftover),
                amount1: amount1Removed.add(amount1CollectedFee).add(pInfo.amount1Leftover),
                originalDepositUsdValue: pInfo.totalDepositUSDValue,
                performanceFeeRecipient: strategyProviderWallet,
                performanceFeeReceivedToken: sInfo.receivedToken,
                performanceFeeRatio: sInfo.performanceFeeRatio,
                serviceFeeRatio: registry.getServiceFeeRatioFromLicenseAmount(sInfo.licenseAmount)
            });

            ///@dev share performance fee with strategy provider
            IShareProfit.ShareProfitOutput memory spOutput = IShareProfit(positionManager).shareProfit(spInput);
            mwInput.amount0Returned = spOutput.amount0Returned;
            mwInput.amount1Returned = spOutput.amount1Returned;
            (mwInput.amount0ReturnedUsdValue, mwInput.amount1ReturnedUsdValue) = _calTokensUsdValue(
                token0,
                token1,
                spOutput.amount0Returned,
                spOutput.amount1Returned
            );
        }

        mwInput.amount0CollectedFee = pInfo.amount0CollectedFee.add(amount0CollectedFee);
        mwInput.amount1CollectedFee = pInfo.amount1CollectedFee.add(amount1CollectedFee);
        IPositionManager(positionManager).middlewareWithdraw(mwInput);

        emit PositionWithdrawan(positionManager, msg.sender, positionId, pInfo.tokenId);
    }

    ///@notice closed position to the position manager with single token
    ///@param positionId ID of closed position
    function singleTokenWithdraw(uint256 positionId, bool isReturnedToken0) external positionIsRunning(positionId) {
        address positionManager = IPositionManagerFactory(registry.positionManagerFactoryAddress())
            .userToPositionManager(msg.sender);
        require(positionManager != address(0), "WRPM0");

        IPositionManager.PositionInfo memory pInfo = IPositionManager(positionManager).getPositionInfo(positionId);

        (address token0, address token1, , , ) = UniswapHelper._getTokens(
            pInfo.tokenId,
            INonfungiblePositionManager(uniswapAddressHolder.nonfungiblePositionManagerAddress())
        );

        ///@dev close position
        (
            uint256 amount0CollectedFee,
            uint256 amount1CollectedFee,
            uint256 amount0Removed,
            uint256 amount1Removed
        ) = IClosePosition(positionManager).closePosition(pInfo.tokenId, false);

        IPositionManager.MiddlewareWithdrawInput memory mwInput;
        mwInput.positionId = positionId;
        if (pInfo.strategyProvider == address(0)) {
            IReturnProfit.ReturnProfitInput memory rpInput = IReturnProfit.ReturnProfitInput({
                token0: token0,
                token1: token1,
                amount0: amount0Removed.add(amount0CollectedFee).add(pInfo.amount0Leftover),
                amount1: amount1Removed.add(amount1CollectedFee).add(pInfo.amount1Leftover),
                returnedToken: isReturnedToken0 ? token0 : token1
            });
            ///@dev return profit to user
            IReturnProfit.ReturnProfitOutput memory rpOutput = IReturnProfit(positionManager).returnProfit(rpInput);
            mwInput.amount0Returned = rpOutput.amount0Returned;
            mwInput.amount1Returned = rpOutput.amount1Returned;
            (mwInput.amount0ReturnedUsdValue, mwInput.amount1ReturnedUsdValue) = _calTokensUsdValue(
                token0,
                token1,
                rpOutput.amount0Returned,
                rpOutput.amount1Returned
            );
        } else {
            address strategyProviderWallet = IStrategyProviderWalletFactory(
                registry.strategyProviderWalletFactoryAddress()
            ).providerToWallet(pInfo.strategyProvider);

            IStrategyProviderWallet.StrategyInfo memory sInfo = IStrategyProviderWallet(strategyProviderWallet)
                .getStrategyInfo(pInfo.strategyId);

            IShareProfit.ShareProfitInput memory spInput = IShareProfit.ShareProfitInput({
                token0: token0,
                token1: token1,
                returnedToken: isReturnedToken0 ? token0 : token1,
                amount0: amount0Removed.add(amount0CollectedFee).add(pInfo.amount0Leftover),
                amount1: amount1Removed.add(amount1CollectedFee).add(pInfo.amount1Leftover),
                originalDepositUsdValue: pInfo.totalDepositUSDValue,
                performanceFeeRecipient: strategyProviderWallet,
                performanceFeeReceivedToken: sInfo.receivedToken,
                performanceFeeRatio: sInfo.performanceFeeRatio,
                serviceFeeRatio: registry.getServiceFeeRatioFromLicenseAmount(sInfo.licenseAmount)
            });

            ///@dev share performance fee with strategy provider
            IShareProfit.ShareProfitOutput memory spOutput = IShareProfit(positionManager).shareProfit(spInput);
            mwInput.amount0Returned = spOutput.amount0Returned;
            mwInput.amount1Returned = spOutput.amount1Returned;
            (mwInput.amount0ReturnedUsdValue, mwInput.amount1ReturnedUsdValue) = _calTokensUsdValue(
                token0,
                token1,
                spOutput.amount0Returned,
                spOutput.amount1Returned
            );
        }

        mwInput.amount0CollectedFee = pInfo.amount0CollectedFee.add(amount0CollectedFee);
        mwInput.amount1CollectedFee = pInfo.amount1CollectedFee.add(amount1CollectedFee);
        IPositionManager(positionManager).middlewareWithdraw(mwInput);

        emit PositionWithdrawan(positionManager, msg.sender, positionId, pInfo.tokenId);
    }

    function _calTokensUsdValue(
        address token0,
        address token1,
        uint256 amount0,
        uint256 amount1
    ) internal view returns (uint256 token0UsdValue, uint256 token1UsdValue) {
        uint24[] memory allowableFeeTiers = registry.getAllowableFeeTiers();
        token0UsdValue = _toUsdValue(token0, amount0, allowableFeeTiers);
        token1UsdValue = _toUsdValue(token1, amount1, allowableFeeTiers);
    }

    function _toUsdValue(
        address tokenAddress,
        uint256 amount,
        uint24[] memory allowableFeeTiers
    ) internal view returns (uint256) {
        address usdTokenAddress = registry.usdValueTokenAddress();

        if (tokenAddress == usdTokenAddress) return amount;

        address deepestPool = UniswapHelper._findV3DeepestPool(
            uniswapAddressHolder.uniswapV3FactoryAddress(),
            tokenAddress,
            usdTokenAddress,
            allowableFeeTiers
        );

        (uint160 sqrtPriceX96, , , , , , ) = IUniswapV3Pool(deepestPool).slot0();

        return UniswapQuoter.quoteWithSqrtPrice(tokenAddress, usdTokenAddress, amount, sqrtPriceX96);
    }
}

contracts/Registry.sol.content:
// SPDX-License-Identifier: GPL-2.0
pragma solidity 0.7.6;
pragma abicoder v2;

import "./interfaces/IRegistry.sol";

/// @title Stores all the governance variables
contract Registry is IRegistry {
    address public override governance;
    address public override positionManagerFactoryAddress;
    address public override strategyProviderWalletFactoryAddress;

    // for security of swap
    int24 public override maxTwapDeviation;
    uint32 public override twapDuration;

    // for quote token
    address public override usdValueTokenAddress;
    address public override weth9;

    // for keeper
    mapping(address => bool) public override whitelistedKeepers;

    // for modules
    mapping(bytes32 => Entry) public modules;
    bytes32[] public moduleKeys;

    // uniswap allowable fee tiers
    mapping(uint24 => bool) public allowableFeeTiers;
    uint24[] public feeTiers;

    // for service fee of strategy provider
    uint32 public override serviceFeeDenominator = 100_000_000;
    address public override serviceFeeRecipient;
    uint32 private serviceFeeRatioLength;
    mapping(uint32 => uint32) public licnesesToServiceFeeRatio;

    ///@notice emitted when governance address is changed
    ///@param newGovernance the new governance address
    event GovernanceChanged(address newGovernance);

    ///@notice emitted when service fee recipient address is changed
    ///@param newServiceFeeRecipient the new service fee recipient address
    event ServiceFeeRecipientChanged(address newServiceFeeRecipient);

    ///@notice emitted when a module is added to registry
    ///@param newContract address of the new contract
    ///@param moduleId keccak of module name
    event ModuleAdded(address newContract, bytes32 moduleId);

    ///@notice emitted when a module address is updated
    ///@param oldContract address of the contract before update
    ///@param newContract address of the contract after update
    ///@param moduleId keccak of contract name
    event ModuleChanged(address oldContract, address newContract, bytes32 moduleId);

    ///@notice emitted when a module address is removed
    ///@param contractAddress address of the removed contract
    ///@param moduleId keccak of removed contract name
    event ModuleRemoved(address contractAddress, bytes32 moduleId);

    constructor(
        address _governance,
        address _serviceFeeRecipient,
        int24 _maxTwapDeviation,
        uint32 _twapDuration,
        address _usdValueTokenAddress,
        address _weth9
    ) {
        require(_governance != address(0), "RCG");
        require(_serviceFeeRecipient != address(0), "RCSFR");
        require(_twapDuration != 0, "RCTD");
        require(_usdValueTokenAddress != address(0), "RCUTA");
        require(_weth9 != address(0), "RCW");

        governance = _governance;
        serviceFeeRecipient = _serviceFeeRecipient;
        maxTwapDeviation = _maxTwapDeviation;
        twapDuration = _twapDuration;
        usdValueTokenAddress = _usdValueTokenAddress;
        weth9 = _weth9;

        initAllowableFeeTiers();
        initServiceRatioes();
    }

    function initAllowableFeeTiers() private {
        feeTiers.push(100);
        allowableFeeTiers[100] = true;
        feeTiers.push(500);
        allowableFeeTiers[500] = true;
        feeTiers.push(3000);
        allowableFeeTiers[3000] = true;
        feeTiers.push(10000);
        allowableFeeTiers[10000] = true;
    }

    function initServiceRatioes() private {
        serviceFeeDenominator = 100_000_000;
        serviceFeeRatioLength = 20;

        // 15.000000%
        licnesesToServiceFeeRatio[1] = 15_000_000;

        // 13.781718%
        licnesesToServiceFeeRatio[2] = 13_781_718;

        // 12.662384%
        licnesesToServiceFeeRatio[3] = 12_662_384;

        // 11.633960%
        licnesesToServiceFeeRatio[4] = 11_633_960;

        // 10.689064%
        licnesesToServiceFeeRatio[5] = 10_689_064;

        // 9.820911%
        licnesesToServiceFeeRatio[6] = 9_820_911;

        // 9.023269%
        licnesesToServiceFeeRatio[7] = 9_023_269;

        // 8.290410%
        licnesesToServiceFeeRatio[8] = 8_290_410;

        // 7.617073%
        licnesesToServiceFeeRatio[9] = 7_617_073;

        // 6.998423%
        licnesesToServiceFeeRatio[10] = 6_998_423;

        // 6.430020%
        licnesesToServiceFeeRatio[11] = 6_430_020;

        // 5.907781%
        licnesesToServiceFeeRatio[12] = 5_907_781;

        // 5.427958%
        licnesesToServiceFeeRatio[13] = 5_427_958;

        // 4.987106%
        licnesesToServiceFeeRatio[14] = 4_987_106;

        // 4.582060%
        licnesesToServiceFeeRatio[15] = 4_582_060;

        // 4.209910%
        licnesesToServiceFeeRatio[16] = 4_209_910;

        // 3.867986%
        licnesesToServiceFeeRatio[17] = 3_867_986;

        // 3.553833%
        licnesesToServiceFeeRatio[18] = 3_553_833;

        // 3.265195%
        licnesesToServiceFeeRatio[19] = 3_265_195;

        // 3.000000%
        licnesesToServiceFeeRatio[20] = 3_000_000;
    }

    ///@notice modifier to check if the sender is the governance contract
    modifier onlyGovernance() {
        require(msg.sender == governance, "ROG");
        _;
    }

    ///@notice change the address of the governance
    ///@param _governance the address of the new governance
    function changeGovernance(address _governance) external onlyGovernance {
        require(_governance != address(0), "RG0");
        governance = _governance;
        emit GovernanceChanged(_governance);
    }

    ///@notice check if the fee tier is allowable
    ///@param feeTier the fee tier to check
    ///@return true if the fee tier is allowable, false otherwise
    function isAllowableFeeTier(uint24 feeTier) external view override returns (bool) {
        return allowableFeeTiers[feeTier];
    }

    ///@notice get the list of fee tiers
    ///@return array of fee tiers
    function getFeeTiers() external view override returns (uint24[] memory) {
        return feeTiers;
    }

    ///@notice get the list of allowable fee tiers
    ///@return array of allowable fee tiers
    function getAllowableFeeTiers() external view override returns (uint24[] memory) {
        uint24[] memory allowableFeeTiersList = new uint24[](feeTiers.length);

        for (uint256 i; i < feeTiers.length; ++i) {
            if (!allowableFeeTiers[feeTiers[i]]) {
                continue;
            }
            allowableFeeTiersList[i] = feeTiers[i];
        }

        return allowableFeeTiersList;
    }

    ///@notice sets the Position manager factory address
    ///@param _positionManagerFactory the address of the position manager factory
    function setPositionManagerFactory(address _positionManagerFactory) external onlyGovernance {
        require(_positionManagerFactory != address(0), "RF0");
        positionManagerFactoryAddress = _positionManagerFactory;
    }

    ///@notice sets the strategy provider collect wallet factory address
    ///@param _strategyProviderWalletFactory the address of the strategy provider collect wallet factory
    function setStrategyProviderWalletFactory(address _strategyProviderWalletFactory) external onlyGovernance {
        require(_strategyProviderWalletFactory != address(0), "RF0");
        strategyProviderWalletFactoryAddress = _strategyProviderWalletFactory;
    }

    ///@notice change the address of the service fee recipient
    ///@param _serviceFeeRecipient the address of the new service fee recipient
    function changeServiceFeeRecipient(address _serviceFeeRecipient) external onlyGovernance {
        require(_serviceFeeRecipient != address(0), "RS0");
        serviceFeeRecipient = _serviceFeeRecipient;
        emit ServiceFeeRecipientChanged(_serviceFeeRecipient);
    }

    function setServiceFeeRatio(uint32 _licenseAmount, uint32 _serviceFeeRatio) external onlyGovernance {
        licnesesToServiceFeeRatio[_licenseAmount] = _serviceFeeRatio;
        if (_licenseAmount > serviceFeeRatioLength) {
            serviceFeeRatioLength = _licenseAmount;
        }
    }

    ///@notice Register a module
    ///@param _id keccak256 of contract name
    ///@param _contractAddress address of the new module
    ///@param _defaultValue default value of the module
    function addNewContract(bytes32 _id, address _contractAddress, bytes32 _defaultValue) external onlyGovernance {
        require(modules[_id].contractAddress == address(0), "RAE");
        require(_contractAddress != address(0), "RA0");
        modules[_id] = Entry({ contractAddress: _contractAddress, defaultData: _defaultValue });
        moduleKeys.push(_id);
        emit ModuleAdded(_contractAddress, _id);
    }

    ///@notice Changes a module's address
    ///@param _id keccak256 of module id string
    ///@param _newModuleAddress address of the new module
    function changeModule(bytes32 _id, address _newModuleAddress) external onlyGovernance {
        require(modules[_id].contractAddress != address(0), "RCE");
        require(_newModuleAddress != address(0), "RCE0");
        address origMoudleAddress = modules[_id].contractAddress;
        modules[_id].contractAddress = _newModuleAddress;
        emit ModuleChanged(origMoudleAddress, _newModuleAddress, _id);
    }

    ///@notice Removes a module
    ///@param _id keccak256 of module id string
    function removeModule(bytes32 _id) external onlyGovernance {
        require(modules[_id].contractAddress != address(0), "RRE");
        address origMoudleAddress = modules[_id].contractAddress;
        delete modules[_id];
        emit ModuleRemoved(origMoudleAddress, _id);
    }

    ///@notice adds a new whitelisted keeper
    ///@param _keeper address of the new keeper
    function addKeeperToWhitelist(address _keeper) external onlyGovernance {
        require(!whitelistedKeepers[_keeper], "RKW");
        whitelistedKeepers[_keeper] = true;
    }

    ///@notice remove a whitelisted keeper
    ///@param _keeper address of the keeper to remove
    function removeKeeperFromWhitelist(address _keeper) external onlyGovernance {
        require(whitelistedKeepers[_keeper], "RKN");
        whitelistedKeepers[_keeper] = false;
    }

    ///@notice Get the keys for all modules
    ///@return bytes32[] all module keys
    function getModuleKeys() external view override returns (bytes32[] memory) {
        return moduleKeys;
    }

    ///@notice Set default data for a module
    ///@param _id keccak256 of module id string
    ///@param _defaultData default data for the module
    function setDefaultData(bytes32 _id, bytes32 _defaultData) external onlyGovernance {
        require(modules[_id].contractAddress != address(0), "RDE");
        require(_defaultData != bytes32(0), "RD0");

        modules[_id].defaultData = _defaultData;
    }

    ///@notice set oracle price deviation threshold
    ///@param _maxTwapDeviation the new oracle price deviation threshold
    function setMaxTwapDeviation(int24 _maxTwapDeviation) external onlyGovernance {
        maxTwapDeviation = _maxTwapDeviation;
    }

    ///@notice set twap duration
    ///@param _twapDuration the new twap duration
    function setTwapDuration(uint32 _twapDuration) external onlyGovernance {
        require(_twapDuration != 0, "RT0");
        twapDuration = _twapDuration;
    }

    ///@notice set address of usd value token
    ///@param _usdValueAddress the address of new usd value token
    function setUsdValueTokenAddress(address _usdValueAddress) external onlyGovernance {
        require(_usdValueAddress != address(0), "RUA");
        usdValueTokenAddress = _usdValueAddress;
    }

    ///@notice set address of weth9
    ///@param _weth9 the address of new weth9
    function setWETH9(address _weth9) external onlyGovernance {
        require(_weth9 != address(0), "RWA");
        weth9 = _weth9;
    }

    ///@notice Get the address of a module for a given key
    ///@param _id keccak256 of module id string
    ///@return entry of the module
    function getModuleInfo(bytes32 _id) external view override returns (Entry memory) {
        return modules[_id];
    }

    ///@notice activates a fee tier
    ///@param fee fee tier to be activated
    function activateFeeTier(uint24 fee) external onlyGovernance {
        require(!allowableFeeTiers[fee], "DRAFT");
        allowableFeeTiers[fee] = true;
        feeTiers.push(fee);
    }

    ///@notice deactivates a fee tier
    ///@param fee fee tier to be deactivated
    function deactivateFeeTier(uint24 fee) external onlyGovernance {
        require(allowableFeeTiers[fee], "DRAFT");
        allowableFeeTiers[fee] = false;
        for (uint256 i; i < feeTiers.length; ++i) {
            if (feeTiers[i] == fee) {
                if (feeTiers.length == 1) {
                    feeTiers.pop();
                    break;
                }
                feeTiers[i] = feeTiers[feeTiers.length - 1];
                feeTiers.pop();
                break;
            }
        }
    }

    ///@notice get service fee ratio for a given license amount
    ///@param _licenseAmount license amount to get service fee ratio
    function getServiceFeeRatioFromLicenseAmount(uint32 _licenseAmount) external view override returns (uint32 ratio) {
        require(_licenseAmount != 0, "RLA0");
        if (_licenseAmount < serviceFeeRatioLength) {
            return licnesesToServiceFeeRatio[_licenseAmount];
        } else {
            return licnesesToServiceFeeRatio[serviceFeeRatioLength];
        }
    }
}

contracts/Storage.sol.content:
// SPDX-License-Identifier: GPL-2.0
pragma solidity 0.7.6;
pragma abicoder v2;

import "./interfaces/IPositionManager.sol";
import "./interfaces/IUniswapAddressHolder.sol";
import "./interfaces/IDiamondCut.sol";
import "./interfaces/IRegistry.sol";

struct FacetAddressAndPosition {
    address facetAddress;
    uint96 functionSelectorPosition; // position in facetFunctionSelectors.functionSelectors array
}

struct FacetFunctionSelectors {
    bytes4[] functionSelectors;
    uint256 facetAddressPosition; // position of facetAddress in facetAddresses array
}

struct StorageStruct {
    // maps function selector to the facet address and
    // the position of the selector in the facetFunctionSelectors.selectors array
    mapping(bytes4 => FacetAddressAndPosition) selectorToFacetAndPosition;
    // maps facet addresses to function selectors
    mapping(address => FacetFunctionSelectors) facetFunctionSelectors;
    // facet addresses
    address[] facetAddresses;
    IUniswapAddressHolder uniswapAddressHolder;
    address owner;
    IRegistry registry;
    mapping(bytes32 => bytes32) storageVars;
}

library PositionManagerStorage {
    bytes32 private constant key = keccak256("position-manager-storage-location");

    ///@notice get the storage from memory location
    ///@return s the storage struct
    function getStorage() internal pure returns (StorageStruct storage s) {
        bytes32 k = key;
        assembly {
            s.slot := k
        }
    }

    ///@notice emitted when a contract changes ownership
    ///@param previousOwner previous owner of the contract
    ///@param newOwner new owner of the contract
    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);

    ///@notice set the owner field on the storage struct
    ///@param _newOwner new owner of the storage struct
    function setContractOwner(address _newOwner) internal {
        require(_newOwner != address(0), "SNO");
        StorageStruct storage ds = getStorage();
        address previousOwner = ds.owner;
        ds.owner = _newOwner;
        if (_newOwner != previousOwner) {
            emit OwnershipTransferred(previousOwner, _newOwner);
        }
    }

    ///@notice make sure that a function is called by the PositionManagerFactory contract
    function enforceIsGovernance() internal view {
        StorageStruct storage ds = getStorage();
        require(msg.sender == ds.registry.positionManagerFactoryAddress(), "SMF");
    }

    ///@notice emitted when a facet is cut into the diamond
    ///@param _diamondCut facet cut
    ///@param _init diamond cut init address
    ///@param _calldata facet cut calldata
    event DiamondCut(IDiamondCut.FacetCut[] _diamondCut, address _init, bytes _calldata);

    ///@notice Internal function version of diamondCut
    ///@param _diamondCut facet cut
    ///@param _init diamond cut init address
    ///@param _calldata facet cut calldata
    function diamondCut(IDiamondCut.FacetCut[] memory _diamondCut, address _init, bytes memory _calldata) internal {
        uint256 _diamondCutLength = _diamondCut.length;
        for (uint256 facetIndex; facetIndex < _diamondCutLength; ++facetIndex) {
            IDiamondCut.FacetCutAction action = _diamondCut[facetIndex].action;
            if (action == IDiamondCut.FacetCutAction.Add) {
                addFunctions(_diamondCut[facetIndex].facetAddress, _diamondCut[facetIndex].functionSelectors);
            } else if (action == IDiamondCut.FacetCutAction.Replace) {
                replaceFunctions(_diamondCut[facetIndex].facetAddress, _diamondCut[facetIndex].functionSelectors);
            } else if (action == IDiamondCut.FacetCutAction.Remove) {
                removeFunctions(_diamondCut[facetIndex].facetAddress, _diamondCut[facetIndex].functionSelectors);
            } else {
                revert("SIF");
            }
        }
        emit DiamondCut(_diamondCut, _init, _calldata);
        initializeDiamondCut(_init, _calldata);
    }

    ///@notice Add functions to facet
    ///@param _facetAddress address of the facet
    ///@param _functionSelectors function selectors to add
    function addFunctions(address _facetAddress, bytes4[] memory _functionSelectors) internal {
        require(_functionSelectors.length != 0, "SNS");
        StorageStruct storage ds = getStorage();
        require(_facetAddress != address(0), "SA0");
        uint96 selectorPosition = uint96(ds.facetFunctionSelectors[_facetAddress].functionSelectors.length);

        // add new facet address if it does not exist
        if (selectorPosition == 0) {
            addFacet(ds, _facetAddress);
        }

        uint256 _functionSelectorsLength = _functionSelectors.length;
        for (uint256 selectorIndex; selectorIndex < _functionSelectorsLength; ++selectorIndex) {
            bytes4 selector = _functionSelectors[selectorIndex];
            address oldFacetAddress = ds.selectorToFacetAndPosition[selector].facetAddress;
            require(oldFacetAddress == address(0), "SFE");
            addFunction(ds, selector, selectorPosition, _facetAddress);
            selectorPosition++;
        }
    }

    ///@notice Add facet by address
    ///@param ds storage struct
    ///@param _facetAddress address of the facet
    function addFacet(StorageStruct storage ds, address _facetAddress) internal {
        ds.facetFunctionSelectors[_facetAddress].facetAddressPosition = ds.facetAddresses.length;
        ds.facetAddresses.push(_facetAddress);
    }

    ///@notice Add single function to facet
    ///@param ds storage struct
    ///@param _selector function selector to add
    ///@param _selectorPosition position of the function selector in the facetFunctionSelectors array
    function addFunction(
        StorageStruct storage ds,
        bytes4 _selector,
        uint96 _selectorPosition,
        address _facetAddress
    ) internal {
        ds.selectorToFacetAndPosition[_selector].functionSelectorPosition = _selectorPosition;
        ds.facetFunctionSelectors[_facetAddress].functionSelectors.push(_selector);
        ds.selectorToFacetAndPosition[_selector].facetAddress = _facetAddress;
    }

    ///@notice Remove single function from facet
    ///@param ds storage struct
    ///@param _facetAddress address of the facet
    ///@param _selector function selector to remove
    function removeFunction(StorageStruct storage ds, address _facetAddress, bytes4 _selector) internal {
        require(_facetAddress != address(0), "SRE");
        require(_facetAddress != address(this), "SRI");

        // replace selector with last selector, then delete last selector
        uint256 selectorPosition = ds.selectorToFacetAndPosition[_selector].functionSelectorPosition;
        uint256 lastSelectorPosition = ds.facetFunctionSelectors[_facetAddress].functionSelectors.length - 1;

        // if not the same then replace _selector with lastSelector
        if (selectorPosition != lastSelectorPosition) {
            bytes4 lastSelector = ds.facetFunctionSelectors[_facetAddress].functionSelectors[lastSelectorPosition];
            ds.facetFunctionSelectors[_facetAddress].functionSelectors[selectorPosition] = lastSelector;
            ds.selectorToFacetAndPosition[lastSelector].functionSelectorPosition = uint96(selectorPosition);
        }

        // delete the last selector
        ds.facetFunctionSelectors[_facetAddress].functionSelectors.pop();
        delete ds.selectorToFacetAndPosition[_selector];

        // if no more selectors for facet address then delete the facet address
        if (lastSelectorPosition == 0) {
            // replace facet address with last facet address and delete last facet address
            uint256 lastFacetAddressPosition = ds.facetAddresses.length - 1;
            uint256 facetAddressPosition = ds.facetFunctionSelectors[_facetAddress].facetAddressPosition;
            if (facetAddressPosition != lastFacetAddressPosition) {
                address lastFacetAddress = ds.facetAddresses[lastFacetAddressPosition];
                ds.facetAddresses[facetAddressPosition] = lastFacetAddress;
                ds.facetFunctionSelectors[lastFacetAddress].facetAddressPosition = facetAddressPosition;
            }
            ds.facetAddresses.pop();
            delete ds.facetFunctionSelectors[_facetAddress].facetAddressPosition;
        }
    }

    ///@notice Replace functions in facet
    ///@param _facetAddress address of the facet
    ///@param _functionSelectors function selectors to replace
    function replaceFunctions(address _facetAddress, bytes4[] memory _functionSelectors) internal {
        require(_functionSelectors.length != 0, "SRF");
        StorageStruct storage ds = getStorage();
        require(_facetAddress != address(0), "SR0");
        uint96 selectorPosition = uint96(ds.facetFunctionSelectors[_facetAddress].functionSelectors.length);

        // add new facet address if it does not exist
        if (selectorPosition == 0) {
            addFacet(ds, _facetAddress);
        }

        uint256 _functionSelectorsLength = _functionSelectors.length;
        for (uint256 selectorIndex; selectorIndex < _functionSelectorsLength; ++selectorIndex) {
            bytes4 selector = _functionSelectors[selectorIndex];
            address oldFacetAddress = ds.selectorToFacetAndPosition[selector].facetAddress;

            require(oldFacetAddress != _facetAddress, "SRR");

            removeFunction(ds, oldFacetAddress, selector);
            addFunction(ds, selector, selectorPosition, _facetAddress);
            selectorPosition++;
        }
    }

    ///@notice remove functions in facet
    ///@param _facetAddress address of the facet
    ///@param _functionSelectors function selectors to remove
    function removeFunctions(address _facetAddress, bytes4[] memory _functionSelectors) internal {
        require(_functionSelectors.length != 0, "SES");

        StorageStruct storage ds = getStorage();

        require(_facetAddress == address(0), "SE0");

        uint256 _functionSelectorsLength = _functionSelectors.length;
        for (uint256 selectorIndex; selectorIndex < _functionSelectorsLength; ++selectorIndex) {
            bytes4 selector = _functionSelectors[selectorIndex];
            address oldFacetAddress = ds.selectorToFacetAndPosition[selector].facetAddress;
            removeFunction(ds, oldFacetAddress, selector);
        }
    }

    ///@notice Initialize the diamond cut
    ///@param _init delegatecall address
    ///@param _calldata delegatecall data
    function initializeDiamondCut(address _init, bytes memory _calldata) internal {
        if (_init == address(0)) {
            require(_calldata.length == 0, "SI0");
        } else {
            require(_calldata.length != 0, "SIC");

            (bool success, bytes memory error) = _init.delegatecall(_calldata);

            if (!success) {
                if (error.length != 0) {
                    revert(string(error));
                } else {
                    revert("SIR");
                }
            }
        }
    }

    ///@notice check to verify that the key is valid and already whitelisted by governance
    ///@param hashedKey key to check
    modifier verifyKey(bytes32 hashedKey) {
        StorageStruct storage ds = getStorage();

        bytes32 storageVariableHash = ds.storageVars[hashedKey];

        require(storageVariableHash == bytes32(uint256(1)), "SDK");
        _;
    }

    ///@notice get a specific slot of memory by the given key and read the first 32 bytes
    ///@param hashedKey key to read from
    function getDynamicStorageValue(bytes32 hashedKey) internal view verifyKey(hashedKey) returns (bytes32 value) {
        assembly {
            value := sload(hashedKey)
        }
    }

    ///@dev supposing we've already set the key on the mapping, we can't insert a wrong key
    ///@notice set a specific slot of memory by the given key and write the first 32 bytes
    ///@param hashedKey key to write to
    ///@param value value to write
    function setDynamicStorageValue(bytes32 hashedKey, bytes32 value) internal verifyKey(hashedKey) {
        assembly {
            sstore(hashedKey, value)
        }
    }

    ///@notice add a new hashedKey to the mapping in storage, sort of whitelist
    ///@param hashedKey key to add to the mapping
    function addDynamicStorageKey(bytes32 hashedKey) internal {
        StorageStruct storage ds = getStorage();

        bytes32 storageVariableHash = ds.storageVars[hashedKey];

        ///@dev return if the key already exists
        if (storageVariableHash != bytes32(0)) return;

        ds.storageVars[hashedKey] = bytes32(uint256(1));
    }
}

contracts/StrategyProviderWallet.sol.content:
// SPDX-License-Identifier: GPL-2.0

pragma solidity 0.7.6;
pragma abicoder v2;

import "./interfaces/IRegistry.sol";
import "./interfaces/IStrategyProviderWallet.sol";
import "./interfaces/IUniswapAddressHolder.sol";
import "@uniswap/v3-core/contracts/interfaces/IUniswapV3Factory.sol";
import "@uniswap/v3-core/contracts/interfaces/IUniswapV3Pool.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/SafeERC20.sol";

contract StrategyProviderWallet is IStrategyProviderWallet {
    using SafeERC20 for IERC20;

    address public immutable owner;
    IRegistry public immutable registry;
    IUniswapAddressHolder public immutable uniswapAddressHolder;

    bytes16[] public strategyIds;
    mapping(bytes16 => StrategyInfo) public strategyIdToStrategyInfo;
    mapping(bytes16 => bool) public isStrategyExist;
    mapping(address => bool) public isReceivedTokenExist;
    address[] private receivedTokens;

    ///@notice emitted when a strategy is added to the wallet
    event StrategyAdded(
        address indexed from,
        bytes16 strategyId,
        address token0,
        address token1,
        uint24 fee,
        uint256 performanceFeeRatio,
        address recievedToken,
        uint32 licenseAmount
    );

    ///@notice emitted when a strategy is updated
    event StrategyUpdated(
        address indexed from,
        bytes16 strategyId,
        address pool,
        uint256 performanceFeeRatio,
        address recievedToken,
        uint32 licenseAmount
    );

    ///@notice emitted when token collected
    event Collect(address token, uint256 amount, address recipient);

    modifier onlyOwner() {
        require(msg.sender == owner, "SPWOO");
        _;
    }

    modifier onlyGovernance() {
        require(msg.sender == registry.governance(), "SPWOG");
        _;
    }

    modifier onlyFactory() {
        require(msg.sender == registry.strategyProviderWalletFactoryAddress(), "SPWOF");
        _;
    }

    modifier strategyNotExist(bytes16 strategyId) {
        require(!isStrategyExist[strategyId], "SPWSE");
        _;
    }

    constructor(address _owner, address _registry, address _uniswapAddressHolder) {
        owner = _owner;
        registry = IRegistry(_registry);
        uniswapAddressHolder = IUniswapAddressHolder(_uniswapAddressHolder);
    }


    ///@notice addStrategy adds a strategy to the wallet
    ///@param strategyId the id of the strategy
    ///@param token0 the first token of the pool
    ///@param token1 the second token of the pool
    ///@param fee the fee of the pool
    ///@param performanceFeeRatio the performance fee ratio of the strategy
    ///@param receivedToken the token that will be received from the strategy
    ///@param licenseAmount the amount of license of listing strategy
    function addStrategy(
        bytes16 strategyId,
        address token0,
        address token1,
        uint24 fee,
        uint24 performanceFeeRatio,
        address receivedToken, // if set 0x0 means received tokens are avaliable in both token0 and token1
        uint32 licenseAmount
    ) external onlyOwner strategyNotExist(strategyId) {
        address pool = IUniswapV3Factory(uniswapAddressHolder.uniswapV3FactoryAddress()).getPool(token0, token1, fee);
        require(pool != address(0), "SPWAP0");

        checkPerformanceFeeRatio(performanceFeeRatio);
        checkLicenseAmount(licenseAmount);

        addReceivedTokens(token0, token1, receivedToken);

        strategyIds.push(strategyId);
        isStrategyExist[strategyId] = true;
        strategyIdToStrategyInfo[strategyId] = StrategyInfo({
            pool: pool,
            performanceFeeRatio: performanceFeeRatio,
            receivedToken: receivedToken,
            licenseAmount: licenseAmount
        });

        emit StrategyAdded(
            msg.sender,
            strategyId,
            token0,
            token1,
            fee,
            performanceFeeRatio,
            receivedToken,
            licenseAmount
        );
    }

    function checkPerformanceFeeRatio(uint24 performanceFeeRatio) internal pure {
        require(performanceFeeRatio <= 10_000, "SPWPFR");
    }

    function checkLicenseAmount(uint32 licenseAmount) internal pure {
        require(licenseAmount > 0, "SPWLA");
    }

    function addReceivedTokens(address token0, address token1, address receivedToken) internal {
        require(receivedToken == token0 || receivedToken == token1 || receivedToken == address(0));

        if (receivedToken == address(0)) {
            if (!isReceivedTokenExist[token0]) {
                receivedTokens.push(token0);
                isReceivedTokenExist[token0] = true;
            }
            if (!isReceivedTokenExist[token1]) {
                receivedTokens.push(token1);
                isReceivedTokenExist[token1] = true;
            }
        } else {
            if (!isReceivedTokenExist[receivedToken]) {
                receivedTokens.push(receivedToken);
                isReceivedTokenExist[receivedToken] = true;
            }
        }
    }

    function updateStrategyReceivedToken(bytes16 strategyId, address receivedToken) external onlyOwner {
        address pool = strategyIdToStrategyInfo[strategyId].pool;
        require(pool != address(0), "SPWNS");
        address token0 = IUniswapV3Pool(pool).token0();
        address token1 = IUniswapV3Pool(pool).token1();
        addReceivedTokens(token0, token1, receivedToken);

        strategyIdToStrategyInfo[strategyId].receivedToken = receivedToken;

        emit StrategyUpdated(
            msg.sender,
            strategyId,
            pool,
            strategyIdToStrategyInfo[strategyId].performanceFeeRatio,
            strategyIdToStrategyInfo[strategyId].receivedToken,
            strategyIdToStrategyInfo[strategyId].licenseAmount
        );
    }

    function getStrategyInfo(bytes16 _strategyId) external view override returns (StrategyInfo memory) {
        return strategyIdToStrategyInfo[_strategyId];
    }

    function collectFromStrategy(bytes16 strategyId, uint256 amount, address recipient) external onlyOwner {
        StrategyInfo memory info = strategyIdToStrategyInfo[strategyId];
        require(info.pool != address(0), "SPWNS");
        _collect(info.receivedToken, amount, recipient);

        emit Collect(info.receivedToken, amount, recipient);
    }

    function collectFromToken(address token, uint256 amount, address recipient) external onlyOwner {
        _collect(token, amount, recipient);

        emit Collect(token, amount, recipient);
    }

    function collectAll(address recipient) external onlyOwner {
        for (uint256 i = 0; i < receivedTokens.length; ++i) {
            uint256 amount = IERC20(receivedTokens[i]).balanceOf(address(this));
            if (amount == 0) {
                continue;
            }
            _collect(receivedTokens[i], amount, recipient);
            emit Collect(receivedTokens[i], amount, recipient);
        }
    }

    function _collect(address token, uint256 amount, address recipient) internal {
        require(token != address(0), "SPWNR");
        require(recipient != address(0), "SPWNR");
        require(amount > 0, "SPWNA");
        IERC20(token).safeTransfer(recipient, amount);
    }
}

contracts/StrategyProviderWalletFactory..content:
// SPDX-License-Identifier: GPL-2.0

pragma solidity 0.7.6;
pragma abicoder v2;

import "./StrategyProviderWallet.sol";
import "./interfaces/IRegistry.sol";
import "./interfaces/IStrategyProviderWalletFactory.sol";
import "./interfaces/IUniswapAddressHolder.sol";
import "@openzeppelin/contracts/math/SafeMath.sol";

contract StrategyProviderWalletFactory is IStrategyProviderWalletFactory {
    using SafeMath for uint256;

    address public immutable registry;
    address public immutable uniswapAddressHolder;

    mapping(address => address) public override providerToWallet;
    mapping(address => bool) public isIncreatorWhitelist;
    address[] public strategyProviderWallets;

    address[] public creatorWhitelist;

    ///@notice emitted when a new strategy provider wallet is created
    ///@param strategyProviderWallet address of StrategyProviderWallet
    ///@param provider address of provider
    event StrategyProviderWalletCreated(address indexed strategyProviderWallet, address creator, address provider);

    modifier onlyGovernance() {
        require(msg.sender == IRegistry(registry).governance(), "SPWFOG");
        _;
    }

    modifier onlycreator() {
        require(isIncreatorWhitelist[msg.sender], "SPWOCWL");
        _;
    }

    constructor(address _registry, address _uniswapAddressHolder) {
        registry = _registry;
        uniswapAddressHolder = _uniswapAddressHolder;
    }

    function addCreatorWhitelist(address _creator) external onlyGovernance {
        require(_creator != address(0), "SPWFA0");
        require(!isIncreatorWhitelist[_creator], "SPWFNICWL");
        isIncreatorWhitelist[_creator] = true;
        creatorWhitelist.push(_creator);
    }

    function create(address provider) external override onlycreator returns (address walletAddress) {
        require(providerToWallet[provider] == address(0), "SPWFA0");
        StrategyProviderWallet wallet = new StrategyProviderWallet(provider, registry, uniswapAddressHolder);
        strategyProviderWallets.push(address(wallet));
        providerToWallet[provider] = address(wallet);

        emit StrategyProviderWalletCreated(address(wallet), address(msg.sender), provider);

        return address(wallet);
    }

    ///@notice get the array of strategy provider wallet addresses
    ///@param cursor is the aforementioned cursor. It simply indicates the starting index for enumeration. The first call should pass 0, and subsequent calls should pass the returned newCursor.
    ///@param howMany indicates how many items should be returned. If there aren’t enough remaining items in the array, the function will return fewer items.
    ///@return wallets address[] return array of StrategyProviderWallet addresses
    ///@return newCursor uint256 return the new cursor
    function getStrategyProviderWallets(
        uint256 cursor,
        uint256 howMany
    ) public view returns (address[] memory wallets, uint256 newCursor) {
        uint256 length = howMany;
        if (length > strategyProviderWallets.length - cursor) {
            length = strategyProviderWallets.length - cursor;
        }

        wallets = new address[](length);
        for (uint256 i = 0; i < length; i++) {
            wallets[i] = strategyProviderWallets[cursor + i];
        }

        return (wallets, cursor + length);
    }

    ///@notice get the length of the strategy provider wallet array
    ///@return uint256 length of the strategy provider wallet array
    function getStrategyProviderWalletsLength() external view returns (uint256) {
        return strategyProviderWallets.length;
    }
}

